pragma solidity ^0.5.17;

import '../node_modules/@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '../node_modules/@openzeppelin/contracts/token/ERC20/ERC20Mintable.sol';
import '../node_modules/@openzeppelin/contracts/token/ERC20/ERC20Pausable.sol';
import '../node_modules/@openzeppelin/contracts/token/ERC20/ERC20Burnable.sol';
import '../node_modules/@openzeppelin/contracts/ownership/Ownable.sol';
import '../node_modules/@openzeppelin/contracts/math/SafeMath.sol';


contract ManaToken is ERC20, ERC20Pausable, ERC20Mintable, ERC20Burnable, Ownable  {
    using SafeMath for uint;
    string public name;
    string public symbol;
    uint8 public decimals; 
    uint256 public _totalSupply;

    constructor() public {
        name = "MANAToken";
        symbol = "MANA";
        decimals = 18;
        _totalSupply = 100000000000000000000000000;
        _mint(msg.sender,100000000000000000000000000);
    }

}
