import React from 'react';
import { newContextComponents } from "@drizzle/react-components";

const { ContractForm } = newContextComponents;

const Purchase = ({drizzle, drizzleState, assetPrice, propAddress, tokenId }) => {
  return(
  <ContractForm
    drizzle={drizzle}
    drizzleState={drizzleState}
    contract={propAddress}
    method="completePurchase" 
    render={({ handleSubmit }) => (
    <Form onSubmit={handleSubmit}>
     <div className='join-form-dialog modal-body'>
       <p>Purchase Asset</p>
      </div> 
      <div className="modal-footer">
        <button className="btn btn-block btn-success join-form-btn" key="submit" type="button" onClick={handleSubmit} style={{ fontSize: 16 }} > 
          Purchase
        </button>
      </div>
    </Form>
  )}
/>
  )
}

export default Purchase;
