import React from 'react';
import { ContractForm, AccountData, ContractData } from "@drizzle/react-components";
import { ToastContainer } from 'react-toastify';

const EthComponent = ({ accounts }) => (
//export default ({ accounts }) => (
<div>
<ToastContainer />
  <h2>Register Account</h2>
  <AccountData accountIndex={0} units={"ether"} precision={3} />

  <h2>Get User</h2>
  <ContractData contract="UserStorage" method="addresses" methodArgs={[accounts[0]]} />

      
 
  <h2>Create User</h2>
  <ContractForm 
    contract="UserController" 
    method="createUser" 
    methodArgs={"Doug",[accounts[0]]}
    labels={['UserName']} 
    //web3.utils.fromAscii("isaiah")
      
  />
</div>
);

export default EthComponent;

