import React from "react";
import Web3 from "web3";
import { Nav  } from "shards-react";
import {  CurrentNetwork, DefaultConntectionBanner, FullConntectionBanner } from '../EthComponents/Web3Components/Web3Network.js';

var web3 = new Web3(Web3.givenProvider);

export default class Footer extends React.Component {

  constructor(props) {
    super(props);



    this.state = {

      networkId: null
    };
  }
  async componentDidMount(){
    const networkId= await web3.eth.net.getId();
    this.setState({ ethNetwork: networkId })
  }

render() {
  const { ethNetwork } = this.state;
    return (    
        <footer>
            <Nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                <div className="container">
                    <a className="navbar-brand" href="#">CAC</a>
                    <div className=""><CurrentNetwork className="nav-link"  ethNetwork={ethNetwork} /></div>
          
                    <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav ml-auto">
                        <li className="nav-item active">
                        <a className="nav-link" href="#">Home <span className="sr-only">(current)</span></a>
                        </li>
                        <li className="nav-item">
                        <a className="nav-link" href="#">FAQ</a>
                        </li>
                        <li className="nav-item">
                        <a className="nav-link" href="#">Blog</a>
                        </li>
                        <li className="nav-item">
                        <a className="nav-link" href="#">About</a>
                        </li>
                        <li className="nav-item">
                        <a className="nav-link" href="#">Contact Us</a>
                        </li>
                    </ul>
                    </div>
                </div>
            </Nav>
        </footer>
       
        );
    }
}
