import React, {useState} from 'react';
import Accordion from 'react-bootstrap/Accordion';
import { Button } from "shards-react";



const NominationDetailsLink = ({ parcel }) => { 



  const [tokenId, setTokenId] = useState();
  const [loading, setLoading] = useState(false);

  const myHeaders = new Headers();
  myHeaders.append("Content-Type", "application/json");

  useEffect(
    () => {
const fetchDetailsLink = async () => {
 
  const graphql= JSON.stringify({ query: 
  `{ parcels(where: {x:"${parcel.x}",y:"${parcel.y}"}) { tokenId }}`
})

  

  let proxyUrl = 'https://cors-anywhere.herokuapp.com/',
  targetUrl = 'https://api.thegraph.com/subgraphs/name/decentraland/marketplace'

  fetch(proxyUrl + targetUrl, {
    method: "POST",
    headers:myHeaders,  
    body:graphql, variables:{},
    redirect: 'follow'
})
    .then(res => res.json())
    .then(
      (result) => {
        const parcelData = result.data.parcels[0];
        //const tokenId = parcelData.tokenId;

        setTokenId(parcelData.tokenId);
        setLoading(false); 
        
        console.log(parcelData.tokenId);
    });
  }
  fetchDetailsLink();
}, []);

return (
    <Accordion defaultActiveKey="1">
      <div>  
        <Accordion.Toggle as={Button} onClick={fetchDetailsLink} variant="link" eventKey="0" outline theme="light">
        <div>Property Details</div>
        </Accordion.Toggle>
          <Accordion.Collapse eventKey="0">
          
            <div className="p-3 mt-3 border rounded">
              <div>
                <a  target="_blank">Decentraland</a>
              </div>
              <div>
                <a href={`https://market.decentraland.org/contracts/0xf87e31492faf9a91b02ee0deaad50d51d56d5d4d/tokens/`+ {tokenId}}  target="_blank">OpenSea</a>
              </div>
            </div>
          </Accordion.Collapse>
      </div> 
    </Accordion>
  )
}

export default NominationDetailsLink;
