  //const Member = <ContractData drizzle={drizzle} drizzleState={drizzleState} contract="ProposalController" method="deployedProposals" methodArgs={[tokenId]} toUtf8 />
  const Member = <ContractData drizzle={drizzle} drizzleState={drizzleState} contract="ProposalController" method="getProposalIdsFromUser" methodArgs={[0]} toUtf8 />
  if(!transactionProcessing)  {  
    
  return (
          <Button className="btn btn-block btn-success contribute-form-btn" key="submit" type="button" onClick={nominateAsset}  theme="success" >
            <p className="mb-0">Nominate</p>{console.log(Member)}
          </Button>
        )   
  } 
  if(transactionProcessing)  {
    return (   
            <div className="mx-auto">   
              <Loader color="#007bff" size="40px" />
            </div>
        )   
  }

}

const mapStateToProps = (state) => ({
  transactionProcessing: state.appReducers.transactionProcessing,
})

const mapDispatchToProps = (dispatch) => ({
  nominationProcess: () =>  dispatch({ type: "NOM_TX_PROCESSING" })
});


export default connect(mapStateToProps, mapDispatchToProps)(NominationButton);
