import { all, fork, takeLatest, put, call, take, race, select, takeEvery } from 'redux-saga/effects';
// const delay = (ms) => new Promise(res => setTimeout(res, ms));

const TOKEN_ID_FETCH = 'TOKEN_ID_FETCH';
const GET_PROP_ADDRESS ='GET_PROP_ADDRESS';
const TEST_SAGA = 'TEST_SAGA';
const TX_SUCCESSFUL = 'TX_SUCCESSFUL';
 const ACCOUNTS_FETCHING = 'ACCOUNTS_FETCHING';
 const CONTRACT_SYNCED = 'CONTRACT_SYNCED'

function* testSaga () {
  console.log('Yes!');
}

function* fetchPropAddr (action) {
  yield put({ type:'GOT_PROP_ADDRESS', propAddress:33});
}

function* appRootSaga () {
  yield all([
  yield takeEvery(GET_PROP_ADDRESS, fetchPropAddr),
  yield takeEvery(TEST_SAGA, testSaga)
])
}


export default appRootSaga;
