import React from 'react';
import OpenSeaApi from "../api/OpenSeaApi.js"

import {
  Card,
  CardDeck,
  CardHeader,
  CardBody,
  CardFooter,
  Button,
  CardTitle,
  Row,
  Col,
  Table,
  Header,
  HeaderCell,
  Container,
  Progress
} from "shards-react";

export default class ProposalCard extends React.Component {
  constructor(){
    super();
    this.state = {
      error:null,
      isLoaded:false,
      assetData: []
    }
  }


  componentDidMount() {
    fetch('https://api.opensea.io/api/v1/events?asset_contract_address=0xf87e31492faf9a91b02ee0deaad50d51d56d5d4d&only_opensea=true&auction_type=english&limit=2')
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            assets: result.asset_events
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, assets } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <Progress />
    } else {
      return (
        <CardDeck className="col-sm-8">
          {assets.map(assetData=> (
            <Card key={assetData.asset.token_id}>
              <CardBody>
                <h5 className="card-title text-center">{assetData.asset.asset_contract.name}</h5>
                  <Row className="asset-image align-items-center">
                    <img className="mx-auto" src= {assetData.asset.image_preview_url}/>
                  </Row>
                  <Row className="mt-2 justify-content-center">
                    <div className="asset-card text-center card-text">{assetData.asset.name}</div>
                  </Row>
                  <Row className="mt-2 justify-content-center">
                    <div className="col-sm asset-card text-left card-text">ETH {assetData.starting_price}</div>
                    <div className="col-sm asset-card text-center card-text">ETH {assetData.starting_price}</div>
                  </Row>
              </CardBody>
              <CardFooter>
                <small className="text-muted">Last updated 3 mins ago</small>
              </CardFooter>
            </Card>          
          ))}

          <style jsx> {` 
            .asset-card{font-weight:400;color:black;font-size:.9rem;font-family: Poppins;}
            `}  
          </style>
        </CardDeck>

    


      );
    }
  }
}
