import React, { Component, useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import { CardFooter,Button } from "shards-react";

const ContributeModal = ({drizzleContext}) => {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  console.log(drizzleContext);

  return ( 
        <>
          <CardFooter className="p-0">
            <button type="button" className="btn btn-lg btn-block card-footer-btn" onClick={handleShow}>Contribute</button>
          </CardFooter>
          
          <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>Fund Asset Purchase</Modal.Title>
            </Modal.Header>
            <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={handleClose}>
                Close
                </Button>
              <Button variant="primary" onClick={handleClose}>
                Fund
              </Button>
            </Modal.Footer>
          </Modal>
        </>
         );    
        } 

    export default ContributeModal;
