import { combineReducers } from 'redux';
import { drizzleReducers } from '@drizzle/store';
import { capRootSaga } from './capSagas';
import { all, fork, takeLatest, put, call, take, race, select, takeEvery } from 'redux-saga/effects';


const TX_DONE = 'TX_DONE'
const TX_PROCESSING = 'TX_PROCESSING'

const initialState = {
  transactionProcessing:false,
  transactionComplete: false
};

function transactionReducer(state = initialState, action) {
    switch (action.type) {
      case TX_PROCESSING:
        return { ...state, transactionProcessing: true };
      case TX_DONE:
        return { ...state, transactionComplete: true };
      default:
        return state;
    }
  }


// function *fetchTransaction() {
