import React from 'react';
import { newContextComponents } from "@drizzle/react-components";
import { Button, Form } from "shards-react";

const { ContractForm, ContractData } = newContextComponents;

const SellAsset = ({drizzle, drizzleState, propAddress, tokenId }) => {
  const account = drizzleState.accounts[0];
  console.log(drizzle.store.getState())

  const reSalePrice = <ContractData drizzle={drizzle} drizzleState={drizzleState} contract={propAddress} method="proposals" methodArgs={[tokenId]} 
      render={data => (
        <>
          {data.reSalePrice}
        </>
      )}
    />

  
  return(
      <ContractForm
        drizzle={drizzle}
        drizzleState={drizzleState}
        contract={propAddress}
        method="sellAsset" 
        sendArgs={{ from:account}}
        render={({ handleSubmit }) => (
        <Form onSubmit={handleSubmit}>
          <div className="justify-content-center mx-auto ">
            <p className="sell-text card-title text-center">Complete Listing Of NFT: {reSalePrice} MANA</p>
          </div> 
          <div className="modal-footer">
            <button className="btn btn-block btn-success join-form-btn" key="submit" type="button" onClick={handleSubmit} style={{ fontSize: 16 }} > 
              Sell NFT
            </button>
          </div>
          <style jsx> {` 
            .sell-text{font-weight:400;color:black;font-size:1rem;font-family: Poppins;} 
            `}    
          </style>
        </Form>
      )}
    />
  )
}

export default SellAsset;
