import React from 'react';
import { connect } from 'react-redux';

class ProposalAddress extends React.Component {
 state = { dataKey: null, drizzleState:null, tokenIdState:null, drizzle:null };

componentDidMount() {
  const { drizzle, tokenIdState, drizzleState } = this.props;
  const dataKey = drizzle.contracts.ProposalController.methods["getPropTokenIdMap"].cacheCall(`${tokenIdState}`);                                            

  this.setState({ dataKey, tokenIdState });
 }
  
 render() {

  const { ProposalController } = this.props.drizzleState.contracts;
  const { dataKey } = this.state;
  const displayData = ProposalController.getPropTokenIdMap[`${dataKey}`];
  const proposalAddress =  displayData && displayData.value;
  getCurrentProp(proposalAddress)
  //this.props.drizzle.store.dispatch({ type: "GOT_PROP_ADDRESS", propAddress:proposalAddress });
   return ( 
          <div>Hello: {proposalAddress}
          </div>
      )
 }

 
}

// export default ProposalAddress;

const mapStateToProps = (state) => ({
  tokenIdState: state.appReducers.tokenId,
  propAddress: state.appReducers.propAddress,
})

const mapDispatchToProps = (dispatch) => ({
  getCurrentProp: (proposalAddress) =>  dispatch({ type: "GOT_PROP_ADDRESS", propAddress:proposalAddress })
});


export default connect(mapStateToProps, mapDispatchToProps)(ProposalAddress);
