import React from "react";
import { newContextComponents } from "@drizzle/react-components";
import { Button, Form } from "shards-react";

const { ContractForm } = newContextComponents;

const JoinForm = ({ onClose, drizzle, drizzleState }) => {
  return (
            <div>  
              <ContractForm
                drizzle={drizzle}
                drizzleState={drizzleState}
                contract="UserController"
                method="createUser" 
                fromAscii
                render={({ inputs, inputTypes, state, handleInputChange, handleSubmit }) => (
                  <Form onSubmit={handleSubmit}>
                   <div className='join-form-dialog modal-body'>
                     <p>Join the Consortium and gain access to tools that help you take advantage passive investment stratigies</p>
                    {inputs.map((input, index) => (
                      <input
                        style={{ fontSize: 30 }}
                        key={input.name}
                        type={inputTypes[index]}
                        name={input.name}
                        value={state[input.name]}
                        placeholder="Username"
                        onChange={handleInputChange}
                      />
                  ))}
                   </div> 
                   <div className="modal-footer">
                    {/* <Button className="btn-dark" onClick={onClose} type="button"  data-dismiss="modal" style={{ fontSize: 14 }} >
                      Close
                    </Button> */}
                    <Button className="btn btn-lg btn-block btn-success" key="submit" type="button" onClick={handleSubmit} style={{ fontSize: 14 }} > 
                      JOIN
                    </Button>
                  </div>
                  </Form>

                )}
              />
            </div>
          )
}

export default JoinForm;
