import React from 'react';

import { composeWithDevTools } from 'redux-devtools-extension';
import createSagaMiddleware from 'redux-saga';
import { createStore, applyMiddleware, compose } from 'redux';
import { DrizzleContext, DrizzleProvider } from "@drizzle/react-plugin";  
import { generateStore, generateContractsInitialState } from "@drizzle/store";
import drizzleOptions  from "../EthComponents/drizzleOptions";

//CREATE STORE
import root from './middleware/sagas/root';
import reducer from './reducers/reducer.js'

//GENERATE STORE
import appRootSaga from './sagas/appSagas';
import appReducer from '.reducers/appReducers.js'
import rootSagas from './sagas/rootSaga';



//APPMIDDLEWARES
import { contractEventSaga, joinTransactionSaga } from './capMiddleWare';
const contractEventNotifier = contractEventSaga;
const joinTransactionComplete = joinTransactionSaga;
const appMiddlewares = [ contractEventNotifier(), joinTransactionComplete ];

//GENERATE STORE
const appReducers = {appReducers: appReducer};
const appSagas = [appRootSaga];


//CREATE STORE
const initialState = {
  contracts: generateContractsInitialState(drizzleOptions)
};

//const sagaMiddleware = createSagaMiddleware();

//const allMiddlewares = [...appMiddlewares, sagaMiddleware];

// const store = createStore(
//       reducer,
//       initialState,
//     composeWithDevTools(
//       applyMiddleware(
//         ...allMiddlewares
//       )
//     )
// );

// sagaMiddleware.run(root);


const store = generateStore({
  drizzleOptions,
  appReducers,
  appSagas,
  //appMiddlewares,
  disableReduxDevTools: false  
})


export default store;
