import React from "react";
import Footer from '../components/Footer.js';
import TestComponent from "../components/testComponent.js";
import { Button, Container  } from "shards-react";
import { Page, Center } from '../components/Layout.js';
import { Link } from '../../routes';
import { DrizzleContext, DrizzleProvider } from "@drizzle/react-plugin";
import {  Loader } from 'rimble-ui';
import options from "../EthComponents/drizzleOptions";
import Accordion from 'react-bootstrap/Accordion';


import { useAccordionToggle } from 'react-bootstrap/AccordionToggle';

//const decoratedOnClick = useAccordionToggle(eventKey, onClick);

export default class TestPage extends React.Component {

render() {
    return (    
            <div>
              <Page>
                <Center>
                <DrizzleContext.Consumer>
                      {drizzleContext => {
                    const { initialized } = drizzleContext;
                          if(!initialized) {
                            return <Loader size="30px" color="blue" />
                            }
                        
                         function ContextAwareToggle({ children, eventKey, callback }) {
  const currentEventKey = useContext(AccordionContext);

  const decoratedOnClick = useAccordionToggle(
    eventKey,
    () => callback && callback(eventKey),
  );

  const isCurrentEventKey = currentEventKey === eventKey;

  return (
    <button
      type="button"
      style={{ backgroundColor: isCurrentEventKey ? 'pink' : 'lavender' }}
      onClick={decoratedOnClick}
    >
      {children}
    </button>
  );
}

function Example() {
  return (
    <Accordion defaultActiveKey="0">
      <Card>
        <Card.Header>
          <ContextAwareToggle eventKey="0">Click me!</ContextAwareToggle>
        </Card.Header>
        <Accordion.Collapse eventKey="0">
          <Card.Body>Hello! I'm the body</Card.Body>
        </Accordion.Collapse>
      </Card>
      <Card>
        <Card.Header>
          <ContextAwareToggle eventKey="1">Click me!</ContextAwareToggle>
        </Card.Header>
        <Accordion.Collapse eventKey="1">
          <Card.Body>Hello! I'm another body</Card.Body>
        </Accordion.Collapse>
      </Card>
    </Accordion>
  );
}

(<Example />);

                    }
                }  
                </DrizzleContext.Consumer>
                </Center>
              </Page>  
            </div>
       
        );
    }

}
