import React, { Component, useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import { CardFooter, Button, Progress, Slider } from "shards-react";
import Web3 from "web3";
import CppContribSlider from '../sliders/CppContributeSlider';
import ContributeForm from '../forms/ContributeForm';
import { ContractData } from "@drizzle/react-plugin";



// export default ({ accounts }) => (
  
// <div>
//   <ToastContainer />

//   <h4>Register Account</h4>
//   <AccountData accountIndex={0} units={"ether"} precision={3} />

//   <h4>Get User</h4>
//   <ContractData contract="UserStorage" method="addresses" methodArgs={[accounts[0]]} />

//   <h4>Create User</h4>
//   <ContractForm contract="UserController" method="createUser" labels={['UserName']} fromAscii />
// </div>

// );  


const ContributeModal = async ({ drizzleState, drizzle, state, handleSlide, propAddress, tokenId, assetPrice, userManaBalance} ) => {


  // const proposalBalance = await drizzle.contracts.ProposalStorage.methods.proposals(`${propAddress}`).call();
  // console.log(proposalBalance)
 
  //let value  = state.value;
  let valueSlider = state.valueSlider;
  let maxRanger = `${assetPrice}`;
  let maxRange = parseInt(maxRanger);
  let minRange = 0;
  let progressMax = maxRange-minRange;
//   <ContractData
//   drizzle={drizzle}
//   drizzleState={drizzleState}
//   contract="ProposalStorage"
//   method="proposals"
//   methodArgs={[propAddress]} 
//   render={displayData => {
//   const values = Object.values(displayData);
//   const balance = Web3.utils.toAscii(values);
//   <div>{balance}</div>   
//     }}
// />
  return ( 
        <>
    <ContractData
  drizzle={drizzle}
  drizzleState={drizzleState}
  contract="ProposalStorage"
  method="proposals"
  methodArgs={[propAddress]} 
  render={displayData => {
  const values = Object.values(displayData);
  //const balance = Web3.utils.toAscii(values);
  <div>{values}</div>   
    }}
/>
     {/* <ContractData contract="ProposalStorage" method="proposals" methodArgs={[propAddress]} /> */}
          <div className="contribution-modal">
            <div className="current-price">
              <Progress value={minRange} max={`${assetPrice}`} >{minRange}</Progress>
              <p className="mt-2 mb-1" >Current Price:&nbsp;{`${assetPrice}`}&nbsp;MANA</p>
              {/* <p className=" mana-balance mt-2 mb-1" >Your MANA Balance:&nbsp;{`${userManaBalance}`}</p> */}
            </div>
            <div>
            <p className="mt-4 mb-1" >Current Pool Depth: {minRange} MANA</p>
              <Slider
                theme="success"
                step={.01}
                pips={{ mode: "range", stepped: true, density: 3 }}
                onSlide={handleSlide}
                connect={[true, false]}
                start={[valueSlider]}
                range={{ min: 0, max: progressMax }}
              />
            </div>  
            <div className="mt-4">{valueSlider} MANA&nbsp;<span className="min-amount-text">(min. 100 MANA)</span></div>               
          </div>
          <div className="modal-footer">
            <ContributeForm state={state} valueSlider={valueSlider} drizzle ={drizzle} handleSlide={handleSlide} />
          </div>
          <style jsx>
            {`  .contribution-modal{font-family: Poppins;}
                .min-amount-text {font-size:.7rem;}
                .mana-balance{font-size:.9rem;color:#66a3ff;}
            `}                
          </style>
        </>
         );    
        } 

    //export default ContributeModal;      


    export default class InitialContributeModal extends Component {
        constructor(props){
        super(props);
        this.handleSlide = this.handleSlide.bind(this),
     
        this.state = {
          error:null,
          isLoaded:false,
          value: 0,
          valueSlider:0,
          contribAmount:0,
          assetData: []
        }
      }
    

      handleSlide(e) {
        this.setState({
          valueSlider: parseFloat(e[0])
        });
      }

      render() {
        return (
        <ContributeModal
          propAddress={this.props.propAddress} 
          userManaBalance={this.props.userManaBalance}
          assetPrice={this.props.assetPrice} 
          drizzleState={this.props.drizzleState} 
          drizzle={this.props.drizzle} 
          tokenId={this.props.tokenId} 
          handleSlide={this.handleSlide} 
          state = {this.state}
        />
        )
      }
      

    }
