import React, { useState, useEffect } from 'react';
import Modal from 'react-bootstrap/Modal';
import NominationForm from '../forms/NominationForm';
import { connect } from 'react-redux';
import Web3 from 'web3';
import ProposalForm from '../forms/ProposalForm';
import { Loader } from 'rimble-ui';

const ForSaleModal = ({ parcel, props, userManaBalance, propAddress, proposalStorage }) => {
  
  const {drizzle, drizzleState} = props;
  const tokenId = parcel.parcel.tokenId;
  const [deposits, setDespositInfo] = useState(null);
  console.log(deposits)
  useEffect(() => {
    fetchProposalInfo();
  }, [])

  const fetchProposalInfo = async () => { 
    
    if(drizzleState.drizzleStatus.initialized){
    const proposalContract = await drizzle.contracts[`${propAddress}`];
    const depositInfoLength = await proposalContract.methods.countDepositers().call();
    var i;
    for (i = 0; i < depositInfoLength; i++) {
      const depositInfo = await proposalContract.methods.depositers([i]).call();
      return console.log(depositInfo);
      setDespositInfo(depositInfo);
    }
    
    }

  }

  //const proposalContract = await drizzle.contracts[`${propAddress}`];

  // const contractData = await proposalContract.methods.proposals(`${tokenId}`).call();
  // const { listPrice, balance, expiresAt } = contractData;
  // const depositAmount = await proposalContract.methods.depositedAmount(drizzleState.accounts[0]).call();
  // const proposalContract = drizzle.contracts[`${propAddress}`];



  const [show, setShow] = useState(false);
  const handleShow = () => setShow(true);
  const handleClose = () => setShow(false);

return (   
      <>   
        <button type="button" className="btn btn-lg btn-block card-footer-btn" onClick={handleShow}>
          NPP NFT
        </button>
        <Modal className="nomintation-modal" show={show} onHide={handleClose}>
          <Modal.Header className="text-center justify-content-center" closeButton>
            <Modal.Title className="text-center justify-content-center">
            <div className="current-price-title">Current Price:&nbsp;{Web3.utils.fromWei(`${parcel.activeOrder.price}`,'ether')}&nbsp;MANA</div>
            <p className="mana-balance mb-1" >Your MANA Balance:&nbsp;<span className="amount-number">{userManaBalance}</span></p>
          </Modal.Title>
        </Modal.Header>
          <Modal.Body className="pt-1 align-items-center">
          {tokenId !== null  ?   
//  {[assetData.asset].map((sub)=> 

deposits
          // deposits.map((depositer) => {
          //   <div>{depositer.amount}</div>
          // })
            // <ProposalForm                  
            //   assetPrice={parcel.activeOrder.price}
            //   expiresAt={parcel.activeOrder.expiresAt}  
            //   nftAddress={parcel.activeOrder.nftAddress}  
            //   props={props}
            //   drizzle={drizzle}
            //   drizzleState={drizzleState}
            //   tokenId={tokenId}
            //   userManaBalance={userManaBalance}
            // /> 
          :
            <div className="align-items-center">   
              <Loader className="mx-auto" color="#007bff" size="40px" />
            </div>
          } 
          </Modal.Body>
        </Modal>
        <style jsx>
          {` 
              .current-price-title{font-size:1.3rem;}
              .nomintation-modal{font-family: Poppins;}
              .card-footer-btn{background:#17c671;color:#ffffff;}
              .btn-lg{border-radius: 0rem;
                border-top-left-radius: 0rem;
                border-top-right-radius: 0rem;
                border-bottom-right-radius: .5rem;
                border-bottom-left-radius: .5rem;}
              .card-footer-btn:hover{background:#e6e6e6;color:#000000;}
              .mana-balance{font-size:.9rem;color:#000;font-weight: 200;text-align:left}
              .amount-number{font-family:Poppins;color:#66a3ff;}
          `}                
        </style>
      </>
        );    
      } 

      const mapStateToProps = (state) => ({
        propAddress: state.appReducers.propAddress,
        proposalStorage: state.appReducers.proposalStorage
      })
           
      const mapDispatchToProps = (dispatch) => ({
        getTokenId: (tokenId) =>  dispatch({ type: "SET_TOKEN_ID", tokenId:tokenId }),
        //getNftAddress: (nftAddress) =>  dispatch({ type: "SET_NftAddress", NftAddress:nftAddress }),
      });
        
        
export default connect(mapStateToProps, mapDispatchToProps)(ForSaleModal);
