import React  from 'react';
import Moment from 'react-moment';
import ReactDOM from 'react-dom';
import Countdown from 'react-countdown';

export default class ProposalEndDate extends React.Component {

  constructor(){
    super();
    this.state = {
      startDate:{}
    }
  }
  

    

    componentDidMount(){
      this.getstartDate();
    }
  
    getstartDate(){
      // Ajax calls here
      const date = new Date();

      this.setState({
        
        startDate:
      
          <Moment add={{ hours: 12 }}>{date}</Moment>
        
      });
    } 
    render() {

    return (
        <div>
            {this.state.startDate} 

            {/* <Countdown  date={start + 1.728e+8}
    >

            </Countdown> */}
        
        </div>
    );
  }
}

