const TX_PROCESSING = 'TX_PROCESSING'
const TX_PROCESSING_DONE = "TX_PROCESSING_DONE";
const TOKEN_ID_FETCHING = 'TOKEN_ID_FETCHING';

// const CHECK_METAMASK = "CHECK_METAMASK";
// const CHECK_METAMASK_DONE = "CHECK_METAMASK_DONE";
