import React from 'react';
import OpenSeaApi from "../api/OpenSeaApi.js"

import {
  Card,
  CardDeck,
  CardHeader,
  CardBody,
  CardFooter,
  Button,
  CardTitle,
  Row,
  Col,
  Table,
  Header,
  HeaderCell,
  Container
} from "shards-react";

export default class ProposalCard extends React.Component {
  constructor(){
    super();
    this.state = {
      error:null,
      isLoaded:false,
      assetData: []
    }
  }


  componentDidMount() {
    fetch('https://api.opensea.io/api/v1/assets?asset_contract_address=0xf87e31492faf9a91b02ee0deaad50d51d56d5d4d&on_sale=true&limit=1')
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            assets: result.assets
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, assets } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <ul>
          {assets.map(asset=> (
            <li key={asset.name}>
              {asset.name} {asset.name}
            </li>
          ))}
        </ul>
      );
    }
  }
}
