// };

// TestComponent.contextType = {
//   drizzle: PropTypes.object
// }

//TestComponent.contextType = DrizzleContext.Context

const mapStateToProps = state => {
  return {
   checkMetaMask: state.transactionReducer.checkMetaMask,
    metaMaskReject: state.transactionReducer.metaMaskReject,
  }
}

const mapDispatchToProps = dispatch => {
  return {
    onMetaMaskCheckDone: () => dispatch({ type: "CHECK_METAMASK_DONE" }),
    onTxErrorDone: () => dispatch({ type: "TX_ERROR_METAMASK_DONE" })
  };
};

export default drizzleConnect(TestComponent,mapDispatchToProps ,mapStateToProps );



