import React, { useState, useEffect } from 'react';
import Modal from 'react-bootstrap/Modal';
import NominationForm from '../forms/NominationForm';
import { connect } from 'react-redux';
import Web3 from 'web3';


const NominationModal = ({ parcel, props,  userManaBalance, getTokenId, transactionProcessing, onSetDrizzleState, getCurrentProp, tokenIdState, propAddress, dispatch, sagaResult, testSaga }) => {

  const [show, setShow] = useState(false);
  const handleShow = () => setShow(true);
  const handleClose = () => setShow(false);
  const [tokenId, setTokenId] = useState(null);
  const [loading, setLoading] = useState(true);
  //const[drizzle, setDrizzle] = useState(drizzle)
  //const [currentProp, setCurrentProp] = useState(null);

  const myHeaders = new Headers();
  myHeaders.append("Content-Type", "application/json");

  useEffect(() => {
  const fetchDetailsLink = async () => {

  const graphql= JSON.stringify({ query: 
  `{ parcels(where: {x:"${parcel.x}",y:"${parcel.y}"}) { tokenId }}`
})

  const proxyUrl = 'https://cors-anywhere.herokuapp.com/',
  targetUrl = 'https://api.thegraph.com/subgraphs/name/decentraland/marketplace'

  fetch(proxyUrl + targetUrl, {
    method: "POST",
    headers:myHeaders,  
    body:graphql, variables:{},
    redirect: 'follow'
  })
    .then(res => res.json())
    .then((result) => {
      const parcelData = result.data.parcels[0];
      return parcelData;
    })
    .then((result) => { 
      const { tokenId } = result;
      if(tokenId === null){
        setLoading(true)
      }
      setLoading(false);
      return tokenId;
    })
    .then((result) => {  
      setTokenId(result);
      getTokenId(result);
      //onSetDrizzleState(drizzle);
      getCurrentProp(result);
      //const currentProp = await drizzle.contracts.ProposalController.methods.getPropTokenIdMap(tokenId).call();
    }) 
  };
fetchDetailsLink();
}, []);

   
return (   
      <> 
          {console.log(sagaResult)}
      <button onClick={() => testSaga}>testSaga</button>
        <button type="button" className="btn btn-lg btn-block card-footer-btn" onClick={handleShow}>Nominate Asset</button>
        <Modal show={show} onHide={handleClose}>
          <Modal.Header className="text-center justify-content-center" closeButton>
            <Modal.Title className="text-center justify-content-center">Asset Price:&nbsp;{parcel.publication.price}&nbsp;MANA</Modal.Title>
          </Modal.Header>
          <Modal.Body className="pt-1">
              <NominationForm                   
                drizzle={props.drizzle}
                drizzleState={props.drizzleState}  
                assetPrice={parcel.publication.price}  
                props={props}
                tokenId={tokenId}
                userManaBalance={userManaBalance}
                /> 
          </Modal.Body>
        </Modal>
      </>
        );    
      } 
        const mapStateToProps = (state) => ({
          sagaResult: state.appReducers.testSaga,
          tokenIdState: state.appReducers.tokenId,
          propAddress: state.appReducers.propAddress
        })
        
        const mapDispatchToProps = (dispatch) => ({
          testSaga: () => dispatch({ type: "TEST_SAGA" }),
          getTokenId: (result) =>  dispatch({ type: "TOKEN_ID_FETCH", tokenId:result }),
          onSetDrizzleState: (drizzle) => dispatch({ type: "SET_DRIZZLE_STATE", drizzle: drizzle }),
          getCurrentProp: (result) =>  dispatch({ type: "GET_PROP_ADDRESS", tokenId:result })
        });
        
        
export default connect(mapStateToProps, mapDispatchToProps)(NominationModal);

    //export default NominationModal;
