import React from 'react';
import { newContextComponents } from "@drizzle/react-components";
import { Button, Form, Container } from "shards-react";
import Web3 from "web3";
import Accordion from 'react-bootstrap/Accordion';

const { ContractForm, ContractData } = newContextComponents;

const SecondSellAsset = ({drizzle, drizzleState, propAddress, tokenId }) => {
  const account = drizzleState.accounts[0];

  return(
    <Container>
       
      <ContractForm
        drizzle={drizzle}
        drizzleState={drizzleState}
        contract={propAddress}
        method="sellAsset" 
        sendArgs={{ from:account}}
        render={({ handleSubmit }) => (
        <Form onSubmit={handleSubmit}>
          <div className="justify-content-center mx-auto">
            <ContractData drizzle={drizzle} drizzleState={drizzleState} contract={propAddress} method="proposals" methodArgs={[tokenId]} 
              render={data => (
                <>
                  {/* <p className="sell-text card-title text-center">The first sale has expired. Submit the second listing at the original purchase price.</p> */}
                  <p className="sell-text card-title text-center">New Sale Price:&nbsp;<span className="amount-number">{Web3.utils.fromWei(`${data.listPrice}`,'ether')}</span>&nbsp;MANA</p>
                  <Accordion defaultActiveKey="1">
          <div>
            <Accordion.Toggle as={Button} size="sm" variant="link" eventKey="0" outline theme="light">
            <div className="mx-1">Details</div>
            </Accordion.Toggle>
              <Accordion.Collapse eventKey="0">
                <div className="mb-3">
                  <small className="text-muted">The first sale has expired. Submit the second listing at the original purchase price.</small>
                </div>
              </Accordion.Collapse>
          </div> 
        </Accordion>
                </>
              )}
            />
          </div>

         
          <div className="modal-footer">
            <button className="btn btn-block btn-success join-form-btn" key="submit" type="button" onClick={handleSubmit} style={{ fontSize: 16 }} > 
              Relist NFT
            </button>
          </div>
          <style jsx> {` 
            .sell-text{font-weight:400;color:black;font-size:1rem;font-family: Poppins;}
            .amount-number {color:#66a3ff;font-family: Poppins} 
            `}    
          </style>
        </Form>
      )}
    />
  </Container>
  ) 
}

export default SecondSellAsset;
