import React from "react";
import { Row,Button, Container  } from "shards-react";
import { Link } from '../../../routes';
import ProposalEndDate from '../component-overview/ProposalCountdown.js';
import moment from 'moment';





export default class ProposalBar extends React.Component {

  constructor(){
    super();
    this.state = {
      error:null,
      isLoaded:false,
      poolStartTime:{}
    }
  }


  componentDidMount() {
    this.getpoolStartTime();
  }

  getpoolStartTime(){
    // Ajax calls here
    this.setState({
      proposalEndTime:
      moment().add(32, 'h').format("YYYY-MM-DDTHH:mm:ssZ"),
      poolStartTime:moment().startOf('week').format("YYYY-MM-DDTHH:mm:ssZ")
    });
  }
 

render() {
    return (  

    <div className="mb-4 d-flex ">
      <div className="card">
        <div className="card-body">
          <Row className="justify-content-sm-center">
          <div className="col d-flex justify-content-sm-center">
            <div>Current Porposals</div>
          </div>  
          <div className="col d-flex justify-content-sm-center">
            <ProposalEndDate timeTillDate={this.state.proposalEndTime} timeFormat="YYYY-MM-DDTHH:mm:ssZ" />
          </div>  
          <div className="col d-flex justify-content-sm-center">
            
          </div>  
          </Row>
        </div>     
      </div>
        <style jsx>{` 
                  .card-text {margin-bottom:.1rem;font-family: Poppins;color:black;}
                  .card {width:70%;margin-bottom:0;}
                  .card-body {padding:.5rem;font-size:18px;font-weight:300;} 
                  .arrow {color:#17c671;font-size:21px;font-weight:900;}
                  `}  
        </style>
    </div>

        );
    }
}
