import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import SellAsset from './SellAsset.js';
import Purchase from './Purchase.js';
import ContributeForm from './ContributeForm';
import { Loader } from 'rimble-ui';
import ForSaleInnerModal from '../modals/ForSaleInnerModal.js';
import AwardAssetBtn from '../buttons/AwardAssetBtn.js';
import SoldAsset from './SoldAsset.js';
import moment from "moment";




const InterMediateForm = ({ drizzle, drizzleState, assetPrice, propAddress, tokenId, soldStatus, proposalBalance }) => {     
  const nowUnix = moment().valueOf();;
  console.log(nowUnix)
  const [status, setProposalSatus] = useState(null);
  const [saleExpiration, setSaleExpiration] = useState(null);

    useEffect(() => {
      fetchProposalStatus();
    }, [propAddress])

    const fetchProposalStatus = async () => { 
      const contract = drizzle.contracts[`${propAddress}`];
      const reSaleExpiresAt = await contract.methods.reSaleExpiresAt().call();
      const dataKey = contract.methods['proposals'].cacheCall(tokenId);
      setSaleExpiration(reSaleExpiresAt);
      setProposalSatus(dataKey);
    }

    const propContracts = drizzleState.contracts[`${propAddress}`];

    //0-FUNDING, 1-FUNDED, 2-WITHDRAWING, 3-PURCHASED, 4-FOR_SALE, 5-SOLD, 6-PAYINGOUT, 7-PAIDOUT, 8-EXPIRED, 9-SALE_FAILED, 10-AWARDED
    if(propContracts.proposals[status]) {
      const displayData = propContracts.proposals[status]; 
      const proposalStatus =  displayData && displayData.value.status;
      console.log(proposalStatus)

    if(proposalStatus.toString() === '0'){
    //0-FUNDING
    return (
        <ContributeForm drizzle={drizzle} drizzleState={drizzleState} assetPrice={assetPrice} propAddress={propAddress} tokenId={tokenId} />
      )
    }
    else if(proposalStatus.toString() === '0') {
      return (
        <div className="modal-body text-deposit-complete justify-content-center">
          <Purchase drizzle={drizzle} drizzleState={drizzleState} assetPrice={assetPrice} propAddress={propAddress} tokenId={tokenId} />
        </div>
      )
    }
  else if(proposalStatus.toString() === '1') {
     //FUNDED
    return (
      <div className="modal-body text-deposit-complete justify-content-center">
        <Purchase drizzle={drizzle} drizzleState={drizzleState} assetPrice={assetPrice} propAddress={propAddress} tokenId={tokenId} />
      </div>
    )
      
    }
    else if(proposalStatus.toString() === '2') {
      //WITHDRAWING
      return  (
        <>
         <div className="mx-auto modal-body justify-content-center"> 
          <p className="text-center">
            Withdrawal in progress...
          </p>  
            <Loader className="mx-auto" color="#007bff" size="40px" />
        </div>
        </>
      )
    }
    else if(proposalStatus.toString() === '3') {
      //PURCHASED
      return  (
        <div className="modal-body text-deposit-complete justify-content-center">
          <SellAsset drizzle={drizzle} drizzleState={drizzleState} propAddress={propAddress} tokenId={tokenId} />
        </div>
      )  
    }
    // else if(proposalStatus.toString() === '4' && soldStatus !== '6') {
    //   //FOR_SALE
    //   return  (
    //     <>
    //       <div className="modal-body text-deposit-complete justify-content-center">
    //         <ForSaleInnerModal 
    //           drizzle={drizzle}
    //           drizzleState={drizzleState} 
    //           propAddress={propAddress}
    //         />
    //       </div>     
    //     </>
    //   )   
    // }
    else if(proposalStatus.toString() === '4' && nowUnix < saleExpiration) {
      //FOR_SALE
      return  (
        <>
          <div className="modal-body text-deposit-complete justify-content-center">
            <AwardAssetBtn 
              drizzle={drizzle}
              drizzleState={drizzleState} 
              propAddress={propAddress}
            />
          </div>     
        </>
      )   
    }
    else if(proposalStatus.toString() === '6' || soldStatus === '6') {
      //SOLD & Paying out
      return  (
        <>
          <div className="modal-body text-deposit-complete justify-content-center">
            {proposalBalance !== null ?
              <SoldAsset 
                drizzle={drizzle} 
                drizzleState={drizzleState} 
                propAddress={propAddress} 
                tokenId={tokenId}  
                proposalBalance={proposalBalance}
                startPay = {true}
              />
            :
              console.log('Loading...')
            }
          </div>
        </>
      )  
    }
    else if(proposalStatus.toString() === '7') {
      //PaidOut
      return  (
        <div className="modal-body text-deposit-complete justify-content-center">
          <p className="text-center">
            NFT Sold and Payout Complete
          </p>
        </div>
      )
    }
  }
  else {
    return (
      <div className="mx-auto">   
        <Loader className="mx-auto" color="#007bff" size="40px" />
      </div>
    )
  }

}

const mapStateToProps = (state) => ({
  soldStatus: state.appReducers.status,
  proposalBalance: state.appReducers.proposalBalance
})

export default connect(mapStateToProps, null)(InterMediateForm);
