import { all, fork, takeLatest, put, call, take, race, select, takeEvery } from 'redux-saga/effects';
import { toast } from 'react-toastify';

const TX_PROCESSING_DONE = "TX_PROCESSING_DONE";
const GOT_TOKEN_ID = 'GOT_TOKEN_ID';
 
function* txSuccessful() {
  console.log('hi')
  yield put({ type: TX_PROCESSING_DONE});
}

function* fetchTokenId() {
  toast.info('hi', { position: toast.POSITION.TOP_RIGHT })
  console.log('hello')
  yield put({ type:GOT_TOKEN_ID,tokenId:33 })
 }

function* appRootSagas() {
  yield takeLatest('TX_SUCCESSFUL', txSuccessful);
  yield takeLatest(GOT_TOKEN_ID, fetchTokenId)
}

const appRootSaga = { appRootSagas };

export default appRootSaga;
