const delay = (ms) => new Promise(res => setTimeout(res, ms));

const TOKEN_ID_FETCHING = 'TOKEN_ID_FETCHING';

function* fetchTokenId (action) {
  //yield put({ type: 'TOKEN_ID_FETCHING', tokenId:22  });
  return console.log(action.tokenId)
}


function* fetchSagas (action) {
  yield takeLatest(AccountsActions.TOKEN_ID_FETCHING, fetchTokenId, action)
}
export default fetchSagas;
