import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import Web3 from "web3";
import { Loader } from 'rimble-ui';
import { Card, CardHeader, CardBody, CardFooter, CardTitle, Button, Row } from "shards-react";
import NominationModal from '../modals/NominationModal';
import FundingModal from '../modals/FundingModal.js';
import SaleModal from '../modals/SaleModal.js';
import SoldModal from '../modals/PaidOutModal';
import PaidOutModal from '../modals/PaidOutModal.js';
import PurchaseModal from '../modals/PurchaseModal.js';
import NominationDetailsLink from '../NominationDetailsLink.js';
import Moment from "react-moment";


const CardFooterButton = ({ props, userManaBalance, getSoldStatus, nftAddress, expiresAt, assetPrice, tokenId, orderId, getOrderId, orderStatus }) => {

  const [isStatus, setStatus] = useState();
  const [isStoredOrderId, setStoredOrderId] = useState('undefined');
  const [isOrderStatus, setOrderStatus] = useState('undefined');
  const [loading, setLoading] = useState(false);

  const oderStatusCall = (orderId) => {

    const myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");
 
  
    const graphql = JSON.stringify({ query: 
      `{
        orders(where: {id:"${orderId}",category:parcel }) {
          status
        }
      }`
  })
  
  const targetUrl = 'https://api.thegraph.com/subgraphs/name/decentraland/marketplace'
  fetch(targetUrl, {
    method: "POST",
    headers:myHeaders,  
    body:graphql, variables:{},
    redirect: 'follow'
  })
    .then(res => res.json())
    .then( async (result) => {
        setOrderStatus(result.data.orders[0].status);
        setLoading(false); 
    },
    (error) => {
    return error;
    }
  )
  }

  const fetchProposals = async () => {
    const proposal = await props.drizzle.contracts.ProposalController.methods.deployedProposals(`${tokenId}`).call();
    if(proposal){
      const proposalAddress = await props.drizzle.contracts.ProposalController.methods.proposalTokenIdMap(`${tokenId}`).call();
      const proposalBalance = await props.drizzle.contracts.MANAToken.methods.balanceOf(`${proposalAddress}`).call();
      const propData = await props.drizzle.contracts.ProposalController.methods.getPropData(`${proposalAddress}`).call();
      const statusString = (propData[0])
      setStatus(statusString.toString());
      const status = propData[0];
      getOrderId(propData[2]);
      setStoredOrderId(propData[2]);

      //test
      oderStatusCall(propData[2]);

      //status 4
      if(status === '4'){
        if(proposalBalance > assetPrice){
          const soldStatus = '6';
          getSoldStatus(proposalBalance, soldStatus);
          setStatus('6');
        }
        else{
            setStatus(status);
            getOrderId(orderId);
        }
      }
      //status 4 End
        else {
          setStatus(status);
          getOrderId(orderId);
        }
      }
      else if (!proposal){
        setStatus()
        getOrderId(orderId);
        setStoredOrderId('undefined')
      }
    }

    useEffect(() => {
      fetchProposals();
    }, [tokenId]);


      if(isStatus === '0' && `"${orderId}"` === `"${isStoredOrderId}"`){
        //0-FUNDING
        return (
            <CardFooter className="p-0">  {isOrderStatus}miss
              <FundingModal 
                nftAddress={nftAddress}
                tokenId={tokenId}
                assetPrice={assetPrice}
                expiresAt={expiresAt}  
                props={props} 
                userManaBalance={userManaBalance}
                orderStatus={orderStatus}
                orderId={orderId}
                storedOrderId={isStoredOrderId}
              />
            </CardFooter> 
          )
        }
      //else if(isStatus === '0' && `'${isStoredOrderId}'` !== 'undifined') {
        else if(isStatus === '0' &&  isOrderStatus === 'cancelled') {
        return (
            <CardFooter className="p-0">  {isOrderStatus}
            {console.log(`"${isStoredOrderId}"`)}
              <NominationModal
                nftAddress={nftAddress}
                tokenId={tokenId}
                assetPrice={assetPrice}
                expiresAt={expiresAt} 
                props={props} 
                orderId={orderId}
                userManaBalance={userManaBalance}
                orderStatus={orderStatus}
                storedOrderId={isStoredOrderId}
              />
            </CardFooter> 
        )
      }
      else if(isStatus === '1') {
        return (
            <CardFooter className="p-0">
              <PurchaseModal 
                nftAddress={nftAddress}
                tokenId={tokenId}
                assetPrice={assetPrice}
                expiresAt={expiresAt} 
                props={props} 
                userManaBalance={userManaBalance}
                orderId={orderId}
                storedOrderId={isStoredOrderId}
              />
            </CardFooter> 
          )
      }
      else if(isStatus === '4') {
        return (
            <CardFooter className="p-0">
              <SaleModal 
                nftAddress={nftAddress}
                tokenId={tokenId}
                assetPrice={assetPrice}
                expiresAt={expiresAt} 
                props={props} 
                userManaBalance={userManaBalance}
                orderStatus={orderStatus}
                orderId={orderId}
                storedOrderId={isStoredOrderId}
              />
            </CardFooter> 
        )
    }
      else if(isStatus === '6') {
        return (
                <CardFooter className="p-0">
                  <SoldModal 
                    nftAddress={nftAddress}
                    tokenId={tokenId}
                    assetPrice={assetPrice}
                    expiresAt={expiresAt} 
                    props={props} 
                    userManaBalance={userManaBalance}
                    orderStatus={orderStatus}
                    orderId={orderId}
                    storedOrderId={isStoredOrderId}
                  />
                </CardFooter> 
            )
      }
      else if(isStatus === '7') {
        return (
                <CardFooter className="p-0">
                  <PaidOutModal
                    nftAddress={nftAddress}
                    tokenId={tokenId}
                    assetPrice={assetPrice}
                    expiresAt={expiresAt} 
                    props={props} 
                    userManaBalance={userManaBalance}
                    orderStatus={orderStatus}
                    orderId={orderId}
                    storedOrderId={isStoredOrderId}
                  />
                </CardFooter> 
            )
      }
      else{
        return (
          <CardFooter className="p-0">
            <NominationModal
              nftAddress={nftAddress}
              tokenId={tokenId}
              assetPrice={assetPrice}
              expiresAt={expiresAt} 
              props={props} 
              orderId={orderId}
              userManaBalance={userManaBalance}
              orderStatus={orderStatus}
              storedOrderId={isStoredOrderId}
            />
          </CardFooter> 
        )
      }
  }
  
  //export default CardFooterButton;

  const mapDispatchToProps = (dispatch) => ({
    getOrderId: (storedOrderId) =>  dispatch({ type: "GOT_STORED_ORDER_ID", storedOrderId: storedOrderId })
  });
    
    
  export default connect(null, mapDispatchToProps)(CardFooterButton);
