import React, { useState, useEffect } from 'react';
import Modal from 'react-bootstrap/Modal';
import NominationForm from '../forms/NominationForm';
import { connect } from 'react-redux';
import Web3 from 'web3';
import ProposalForm from '../forms/ProposalForm';
import { Loader } from 'rimble-ui';

const NominationModal = ({ parcel, props, userManaBalance, getNftAddress }) => {

  const {drizzleState, drizzle} = props;

  const [show, setShow] = useState(false);
  const handleShow = () => setShow(true);
  const handleClose = () => setShow(false);
  const [tokenId, setTokenId] = useState(null);

  //graphql call
  // const myHeaders = new Headers();
  // myHeaders.append("Content-Type", "application/json");

  useEffect(() => {
  const fetchDetailsLink = async () => {

    const tokenId = await drizzle.contracts.LANDRegistry.methods.encodeTokenId(parcel.x, parcel.y).call();

    const nftAddress = '0x3f27363c9f86E8ac151a0D332abC2C3b60B7bCf5';
    setTokenId(tokenId);
    getNftAddress(nftAddress);
  
  //graphql call
  // const graphql= JSON.stringify({ query: 
  //   `{ parcels(where: {x:"${parcel.x}",y:"${parcel.y}"}) 
  //     {
  //       nft{
  //         activeOrder{
  //             Order:
  //             nftAddress
  //             price
  //         }
  //         tokenId
  //       }
  //     }
  //   }`
  // })

  //graphql call
  // const proxyUrl = 'https://cors-anywhere.herokuapp.com/',
  // targetUrl = 'https://api.thegraph.com/subgraphs/name/decentraland/marketplace'

  // fetch(proxyUrl + targetUrl, {
  //   method: "POST",
  //   headers:myHeaders,  
  //   body:graphql, variables:{},
  //   redirect: 'follow'
  // })
  //   .then(res => res.json())
  //   .then((result) => {
  //     const parcelData = result.data.parcels[0];
  //     return parcelData;
  //   })
  //   .then((result) => { 
  //     const { nft } = result;
  //     const { tokenId } = nft;
  //     //const nftAddress = nft.activeOrder.Order
  //     const nftAddress = '0x3f27363c9f86E8ac151a0D332abC2C3b60B7bCf5';
  //     setTokenId(tokenId);
  //     getNftAddress(nftAddress);
  //   })
  };
fetchDetailsLink();

}, []);

return (   
      <> 
        <button type="button" className="btn btn-lg btn-block card-footer-btn" onClick={handleShow}>
          Nominate Asset
        </button>
        <Modal className="nomintation-modal" show={show} onHide={handleClose}>
          <Modal.Header className="text-center justify-content-center" closeButton>
            <Modal.Title className="text-center justify-content-center">Current Price:&nbsp;{parcel.nft.activeOrder.price}&nbsp;MANA
            <p className="mana-balance mb-1" >Your MANA Balance:&nbsp;<span className="amount-number">{`${parcel.nft.activeOrder.price}`}</span></p>
          </Modal.Title>
        </Modal.Header>
          <Modal.Body className="pt-1 align-items-center">
         {tokenId !== null ?   
          <ProposalForm                  
            assetPrice={parcel.nft.activeOrder.price}  
            props={props}
            drizzle={drizzle}
            drizzleState={drizzleState}
            tokenId={tokenId}
            userManaBalance={userManaBalance}
            /> 
          :
            <div className="align-items-center">   
            {tokenId} Nomination Modal 76
              <Loader className="mx-auto" color="#007bff" size="40px" />
            </div>
          } 
          </Modal.Body>
        </Modal>
        <style jsx>
          {`  .nomintation-modal{font-family: Poppins;}
              .mana-balance{font-size:.9rem;color:#000;font-weight: 200;text-align:left}
              .amount-number{font-family:Poppins;color:#66a3ff;}
          `}                
        </style>
      </>
        );    
      } 
  
        const mapDispatchToProps = (dispatch) => ({
          getTokenId: (tokenId) =>  dispatch({ type: "SET_TOKEN_ID", tokenId:tokenId }),
          getNftAddress: (nftAddress) =>  dispatch({ type: "SET_NftAddress", NftAddress:nftAddress }),
        });
        
        
export default connect(null, mapDispatchToProps)(NominationModal);
