import { Box, Modal, Button, Text, Card, CardBody, CardHeader } from "shards-react";
import { Flex } from "rimble-ui";

export default ({ onClose } ) => {

  const openModal = e => {
    e.preventDefault();
    setIsOpen(true);
  };

    return (
      <Card p={4}>
        <CardBody>
      
            <CardHeader  width={"420px"} p={0}>
              <Button.Text
                icononly
                icon={"Close"}
                color={"moon-gray"}
                position={"absolute"}
                top={0}
                right={0}
                mt={3}
                mr={3}
                onClick={onClose} 
              />
              </CardHeader >
              <CardHeader  p={4} mb={3}>
                <h3>Join</h3>
                <Text>children</Text>
              </CardHeader >
  
              <Flex
                px={4}
                py={3}
                borderTop={1}
                borderColor={"#E8E8E8"}
                justifyContent={"flex-end"}
              >
                <Button.Outline onClick={onClose} >Cancel</Button.Outline>
                <Button ml={3}>Confirm</Button>
              </Flex>
            </CardBody>
      
        </Card>
    
    );




  };
