import React from 'react';
import { connect } from 'react-redux';
import Accordion from 'react-bootstrap/Accordion';
import { Button, Row, Form } from "shards-react";
import { newContextComponents } from "@drizzle/react-components";
import Web3 from "web3";
const { ContractForm } = newContextComponents;


const Input = ({ value, onChange, placeholder }) => (
  <div>
    <input className="form-control form-control-sm" value={value} onChange={onChange} placeholder={placeholder}  /> 
  </div>
) 

const UpdatePrice =  ({ drizzle, drizzleState, assetPrice, listPrice, propAddress }) => { 

const update = async (e) => {
    e.preventDefault() 
  
    for (let key in this.state) {
      if (!this.state[key]) {
        return toast.warning( `You must fill in your ${key}!`, { position: toast.POSITION.TOP_RIGHT });
      }
    }

      if(!userNameBool)  {
      const contract = await drizzle.contracts[`${propAddress}`];
     
      try {
      contract.methods["updatePrice"].cacheSend(assetPrice);
    } catch (err) {
      console.error("Err:", err)
    }
  }
      else {
        return toast.warning( `Username ${username} is already in use`, { position: toast.POSITION.TOP_RIGHT });
      }
  }

  console.log(assetPrice)

if(listPrice != assetPrice ) {  
return (
    <Accordion defaultActiveKey="1">
      <Row className=" mt-2 mb-1 justify-content-center">
        <div>
          <Accordion.Toggle as={Button} className="px-2.5 py-1.5" variant="link" eventKey="0" theme="warning" outline >
          <div className="amount-text" >Update Price:</div>
          <div className="amount-text" >Current Price: <span className="amount-number">{Web3.utils.fromWei(`${assetPrice}`,'ether')}</span> MANA</div>
          <div className="amount-text" >Original Price: <span className="original-amount-number">{Web3.utils.fromWei(`${listPrice}`,'ether')}</span> MANA</div>
          </Accordion.Toggle>
            <Accordion.Collapse eventKey="0">
              <Row className="justify-content-center text-left">
                <Form onSubmit={update}>
                  <div className='join-form-dialog modal-body'>
                    <p>Update Price</p>
                    <input className="form-control form-control-sm" value={assetPrice}  /> 
                  </div> 
                  <div className="modal-footer">
                    <Button outline theme="warning" size="sm" className="mt-2 mx-auto"
                      key="submit"
                      type="button"
                      onClick={update} 
                      style={{ fontSize: 12 }}
                    >
                      Update Price
                    </Button>
                  </div>
                </Form>
              </Row>
            </Accordion.Collapse>
        </div> 
      </Row>
      <style jsx> {` 
          .details-btn:hover{font-family: Poppins;font-size:.9rem;}
          .amount-text {font-size:.9rem;color:#000;font-family: Poppins;}
          .amount-number {font-size:.9rem;color:#66a3ff;font-family: Poppins}
          .original-amount-number{font-size:.9rem;color:#c4183c;font-family: Poppins}
        `}    
      </style>
    </Accordion>
  )
}
  else  {
    return null;
  }

}


const mapStateToProps = (state) => ({
  listPrice:state.appReducers.listPrice
})



export default connect(mapStateToProps, null)(UpdatePrice);

