import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import { CardFooter } from "shards-react";
import Web3 from "web3";
import InitialContribForm from '../../EthComponents/InitialContribForm';


const InitialProposalModal = ( { drizzleState, drizzle, assetData, state, show, handleClose } ) => {
  
  // const [show, setShow] = useState(false);
  // const handleClose = () => setShow(false);
  // const handleShow = () => setShow(true);

  // const [close, setClose] = useState(true);
  // const handleClose = () => setClose(false);
  // const handleShow = () => setShow(true);

  let valueSlider = state.valueSlider;
  return (   
        <>
          {/* <CardFooter className="p-0">
            <button type="button" className="btn btn-lg btn-block card-footer-btn" onClick={handleShow}>Nominate Asset</button>
          </CardFooter> */}
          {assetData.sell_orders.map((subOne)=> 
            {if(subOne.payment_token_contract.id === 1){
              const assetPrice = Web3.utils.fromWei(subOne.current_price,'ether');
              return (
                <Modal  key={subOne.created_date.toString()} show={show}>

                  <Modal.Header>
                    <Modal.Title id="contained-modal-title-vcenter">Asset Price:&nbsp;{assetPrice}&nbsp;ETH</Modal.Title>
                        <button onClick={() => this.setState({ show: handleShow })}  type="button" className="close" data-dismiss="modal" aria-label="Close">
              <span aria-hidden="true">&times;</span>
          </button>
                  </Modal.Header>
                  <Modal.Body>
                    <div>
                      <div>
                        <div>
                        <InitialContribForm 
                          state={state} 
                          valueSlider={valueSlider} 
                          drizzle = { drizzle }
                          drizzleState = { drizzleState }  
                          assetPrice = {assetPrice}  
                          /> 
                        </div>  
                      </div>
                    </div>
                  </Modal.Body>
                </Modal>
                   )
                  }
                }
              )}
        </>
         );    
        } 

    export default InitialProposalModal;
