import React from 'react';
import { ContractForm, AccountData, ContractData } from "@drizzle/react-components";
import Web3 from "web3"

class EthAccounts extends React.Component {
 state = { dataKey: null, userId: null,user: null,id: null };

 async componentDidMount() {
   const { drizzle, drizzleState } = this.props;
   const contract = drizzle.contracts.UserStorage;
   const account = drizzleState.accounts[0];
                                                        
  const user = contract.methods['addresses'].cacheCall(account);
  const id = drizzleState.contracts.UserStorage.addresses[user]; 
  const userId = id && id.value[0];

  //const userId = await drizzle.contracts.UserStorage.methods.addresses(account).call();
 
 
  const dataKey = await contract.methods["profiles"].cacheCall(1);
  this.setState({ dataKey, userId });

 }


 render() {
  
   const { UserStorage } = this.props.drizzleState.contracts;
   const displayData = UserStorage.profiles[this.state.dataKey]; // if displayData (an object) exists, then we can display the value 
   console.log(this.state.userId)
   //const userId = UserStorage.addresses[this.state.user];   
  //console.log(userId && userId.value[0]);

  //const username = displayData && displayData.value[1];
  //const userface = Web3.utils.toAscii(username);
  
   //username: Web3.utils.toAscii(username),

   return (
    
     <p>Hi: {displayData && displayData.value[1]}</p>
   )
 }
}

export default EthAccounts

