import Web3 from "web3";
import Ownable from "../artifacts/contracts/Ownable.json";
import ContractManager from "../artifacts/contracts/ContractManager.json";
import BaseStorage from "../artifacts/contracts/BaseStorage.json";
import UserStorage from "../artifacts/contracts/UserStorage.json";
import ProposalStorage from "../artifacts/contracts/ProposalStorage.json";
import BaseController from "../artifacts/contracts/BaseController.json";
import ProposalController from "../artifacts/contracts/ProposalController.json";
import UserController from "../artifacts/contracts/UserController.json";

const options = {
  // web3: {
  //   block: false,
  //   customProvider: new Web3("ws://localhost:7545"),
  // },
  contracts: [UserStorage,BaseController,Ownable,ContractManager,BaseStorage,ProposalStorage,ProposalController,UserController],
  events: {
    UserStorage: ["UserStorage"]
  },
  polls: {
    accounts: 1500,
  },

};

export default options;
