import  React, { Component } from "react";
import PurchaseProtocal from '../components/PurchaseProtocal.js';
import { Page, Center } from '../components/layout/Layout.js';
import { DrizzleContext } from "@drizzle/react-plugin";
import {  Loader } from 'rimble-ui';

export default class Cpp extends Component {

render() {
    return (    
            <div className="text-all">
              <Page>
                <Center>
                  <DrizzleContext.Consumer>
                    {drizzleContext => {
                     const {drizzle, drizzleState, initialized } = drizzleContext;
                        if(!initialized) {
                          return <Loader className="mt-2 mx-auto" size="30px" color="blue" />
                          }                       
                          return(                               
                            <div> <PurchaseProtocal  drizzleState={drizzleState} drizzle={drizzle}  /></div>  
                          )
                       }
                      }  
                  </DrizzleContext.Consumer>
                </Center>
              </Page>    
              <style jsx> {` 
                  .details-btn:hover{font-family: Poppins;font-size:.9rem;}
                  .body {font-size:.9rem;color:#000;font-family: Poppins;}
                  .amount-number {font-size:.9rem;color:#66a3ff;font-family: Poppins}
                `}    
              </style>
            </div>
            
       
        );
    }

}
