import React from "react";
import { Button, Container  } from "shards-react";
import { Link } from '../../routes';
import { DrizzleContext } from "@drizzle/react-plugin";
import { drizzleConnect } from 'drizzle-react';
import PropTypes from 'prop-types';
import connect from 'react-redux';

class TestComponent extends React.Component {
  static contextTypes = DrizzleContext.Context;
  constructor(props, context) {
    super(props);
    this.context = context;
    //console.log(this.contracts)
  }

render() {
  
  //console.log(this.contracts,'hi')
    return (  

    <div className="d-flex justify-content-center">
      <div className="card">
        <div className="card-body">
          <Container>
              <div className="row justify-content-sm-center">  
                <div className="col-sm d-flex justify-content-sm-center">
                  <div className="card-text">Pool Depth </div>
                  <span className="dia-total px-3">$1,234,559</span>
                </div>    
                <div className="col-sm d-flex justify-content-sm-center">
                  <div className="card-text interest">Interest </div>
                  <span className="dia-interest card-text px-3">$2,456</span>
                </div>                                
                <div className="col-sm d-flex justify-content-sm-center">
                  <div>
                    <Link route={`/interestpool`}>
                      <Button className="joinButton" outline theme="success" size="sm">
                          <i className="far fa-button" />APP &rarr;
                      </Button>                                   
                    </Link>  
                  </div>
                </div>
              </div>
            </Container>
          </div>     
        </div>
        <style jsx>{` .card-text {margin-bottom:.1rem;font-family: Poppins;}
                  .card {font-size: 10px;width:70%;margin-bottom:0;}
                  .card-header {font-family: Poppins;padding: .3rem 2.2rem;background-color: #f2f2f2;font-size:17px;font-weight:500;}
                  .card-body {padding: .6rem;font-size:17px;font-weight:400;} 
                  .joinButton {letter-spacing: 2.5px;}
                  `}  
        </style>
    </div>

        );
    }
    
}

// TestComponent.propTypes = {
//   classes: PropTypes.object.isRequired,
// };

// TestComponent.contextType = {
//   drizzle: PropTypes.object
// }

//TestComponent.contextType = DrizzleContext.Context;

const mapStateToProps = state => {
  return {
    checkMetaMask: state.capReducers.checkMetaMask,
    metaMaskReject: state.capReducers.metaMaskReject,
  }
}

const mapDispatchToProps = dispatch => {
  return {
    onMetaMaskCheckDone: () => dispatch({ type: "CHECK_METAMASK_DONE" }),
    onTxErrorDone: () => dispatch({ type: "TX_ERROR_METAMASK_DONE" }),
    onRedirectToHomeDone: () => dispatch({ type: "REDIRECT_TO_HOME_DONE"})
  };
};

export default connect(TestComponent, mapStateToProps,mapDispatchToProps);



//export default TestComponent;
