import React, { useState, useEffect } from 'react';
import Pagination from '../../components/Pagination';
import Nominations from '../../components/Nominations'
//import Moment from "react-moment";
import moment from "moment";

//moment().valueOf();
// var CurrentDate = moment().toISOString();
var CurrentDate = moment().valueOf();
// moment().valueOf()

// for UNIX time-stamp in milliseconds

// moment().format('x') // lowerCase x

// for UNIX time-stamp in seconds moment().format('X') // capital X


const NominationAssetCards = ({ props, userManaBalance }) => {

  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [parcelsPerPage] = useState(6);
  const [totalOrders, setTotalOrders] = useState();

  const myHeaders = new Headers();
  myHeaders.append("Content-Type", "application/json");
  const currentTime= moment().valueOf();
  const graphql= JSON.stringify({ query: 
      `{
        nfts(first: 8, orderBy: searchOrderPrice, where:{ category: parcel, searchOrderStatus: open,searchOrderExpiresAt_gt:1604016000000}) {
          
          parcel {
            x,
            y, 
            tokenId
          }
          activeOrder {
            price
            expiresAt
            nftAddress
            txHash
          }
        }
      }`
    })

  useEffect(
    () => {
    
  
    const fetchParcels = async () => {
    //const proxyUrl = 'https://cors-anywhere.herokuapp.com/',
    const targetUrl = 'https://api.thegraph.com/subgraphs/name/decentraland/marketplace'
  
    fetch(targetUrl, {
      method: "POST",
      headers:myHeaders,  
      body:graphql, variables:{},
      redirect: 'follow'
    })
      .then(res => res.json())
      .then( async (result) => {
          const orders = await result.data.nfts;
          setOrders(orders);
          setTotalOrders(orders.length)
          setLoading(false); 
        },
        (error) => {
          setLoading(true),
          error;
        }
    ) 
  }
    fetchParcels();  
  }, []);


  // Get current orders
  const indexOfLastParcel = currentPage * parcelsPerPage;
  const indexOfFirstParcel = indexOfLastParcel - parcelsPerPage;
  const currentParcels = orders.slice(indexOfFirstParcel, indexOfLastParcel);

  // Change page
  const paginate = pageNumber => setCurrentPage(pageNumber);

  return (   
    <>
    {CurrentDate}
      <Nominations 
        orders={currentParcels} 
        loading={loading} 
        props={props} 
        userManaBalance={userManaBalance}
      />
       <Pagination
        currentPage ={currentPage }
        parcelsPerPage={parcelsPerPage}
        totalParcels={totalOrders}
        paginate={paginate}
      />
    </>
  );
};


export default NominationAssetCards;
