import { connect } from 'react-redux';
import { Container  } from "shards-react";
import ProposalCards from "./cards/ProposalCards.js";
import NominationAssetCards from "./cards/NominateAssetCards.js";
import Web3 from "web3";

import { newContextComponents } from "@drizzle/react-components";
const { ContractData, ContractForm } = newContextComponents;

let web3 = new Web3(Web3.givenProvider);

class PurchaseProtocal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      manaBalance:null
    }
  }

  async componentDidMount() {   
    const { drizzle, drizzleState } = this.props;
    const account = drizzleState.accounts[0];
    const userManaBalanceConvert = await drizzle.contracts.MANAToken.methods.balanceOf(account).call();
    const userManaBalanceOriginal = web3.utils.fromWei(`${userManaBalanceConvert}`,'ether');
    const userManaBalance = Number.parseFloat(userManaBalanceOriginal).toFixed(2);
    this.setState({
      manaBalance: userManaBalance
    });
  }

  render() {       
    return (
    <div className="d-flex justify-content-center">

<ContractForm
              drizzle={this.props.drizzle}
              drizzleState={this.props.drizzleState}
              contract="Escrow"
              method="transfer"
            />
            <ContractForm
              drizzle={this.props.drizzle}
              drizzleState={this.props.drizzleState}
              contract="Escrow"
              method="deposit"
            />

<ContractForm
              drizzle={this.props.drizzle}
              drizzleState={this.props.drizzleState}
              contract="Escrow"
              method="deposit"
              render={({ inputs, inputTypes, state, handleInputChange, handleSubmit }) => (
                <form onSubmit={handleSubmit}>
                  {inputs.map((input, index) => (
                    <input
                      style={{ fontSize: 30 }}
                      key={input._value}
                      type={inputTypes[index]}
                      name={input._value}
                      value={state[input._value]}
                      placeholder={input._value}
                      onChange={handleInputChange}
                    />
                  ))}
                  <button
                    key="submit"
                    type="button"
                    onClick={handleSubmit}
                    style={{ fontSize: 30 }}
                  >
                    Submit Big
                  </button>
                </form>

              )}
            />
            <ContractForm
              drizzle={this.props.drizzle}
              drizzleState={this.props.drizzleState}
              contract="Escrow"
              method="transferFrom"
            />

      <Container>
        {/* <NominationAssetCards
          props={this.props} 
          userManaBalance ={this.state.manaBalance}
        /> */}
        {/* <NominateAssetCards drizzleState ={this.props.drizzleState} drizzle={this.props.drizzle} txSuccessful={this.props.txSuccessful}  /> */}
        {/* <ProposedAssetCard drizzleState ={this.props.drizzleState} drizzle={this.props.drizzle} /> */}
        {/* <ProposalCards drizzleState ={this.props.drizzleState} drizzle={this.props.drizzle} txSuccessful={this.props.txSuccessful} /> */}
      </Container>
      
      <style jsx>{` .card-text {margin-bottom:.1rem;font-family: Poppins;}
                .card {font-size: 10px;width:70%;margin-bottom:0;}
                .card-header {font-family: Poppins;padding: .3rem 2.2rem;background-color: #f2f2f2;font-size:17px;font-weight:500;}
                .card-body {padding: .6rem;font-size:17px;font-weight:400;} 
                .joinButton {letter-spacing: 2.5px;}
                `}  
      </style>
    </div>
    )
        
    }
     
}
  
const mapStateToProps = (state) => ({
 propAddress: state.appReducers.propAddress
})

const mapDispatchToProps = (dispatch) => ({
  //onSetDrizzleState: (drizzleStateSet) => dispatch({ type: "SET_DRIZZLE_STATE", drizzle: drizzleStateSet }),
  // getTokenId: (result) =>  dispatch({ type: "TOKEN_ID_FETCH", tokenId:result }),
   //getCurrentProp: (drizzle) =>  dispatch({ type: "GET_PROP_ADDRESS",drizzle:drizzle })
});

export default connect(mapStateToProps, mapDispatchToProps)(PurchaseProtocal);
