import React, { useState, useEffect } from 'react';
import Web3 from 'web3';
import { Loader } from 'rimble-ui';
import { Card, CardHeader, CardBody, CardFooter, CardTitle, Button, Row } from "shards-react";

const ForSaleModal = ({ drizzle, propAddress }) => {
  
  const [deposits, setDespositInfo] = useState([]);
  const [show, setShow] = useState(false);
  const handleShow = () => setShow(true);
  const handleClose = () => setShow(false);

  useEffect(() => {
    fetchProposalInfo();
  }, [])

  const fetchProposalInfo = async () => { 
    let state = drizzle.store.getState()
    if(state.drizzleStatus.initialized){
    const proposalContract = await drizzle.contracts[`${propAddress}`];
    const depositInfoLength = await proposalContract.methods.countDepositers().call();

    let i;
    let depositerArray = [];
    for (i = 0; i < depositInfoLength; i++) {
    const depositInfo = await proposalContract.methods.depositers([i]).call();
    depositerArray.push([depositInfo.depositer, depositInfo.amount]);
    setDespositInfo(depositerArray);
    }
   
  }
   return 'Loading....'

  }

return (   
      <>   
            {deposits ?   
                <div className="mb-4 col-sm-12 col-md-12 col-lg-4">
                  {deposits.map(deposit => (
                    <div key={deposit[0]} >
                      <Row className="mt-2 justify-content-center"> 
                        <p className="deposit-text mb-1 asset-card text-center card-text">
                          {deposit[0]}
                        </p>
                        <p className="amount-number mb-1 asset-card text-center card-text">
                          {Web3.utils.fromWei(deposit[1],'ether')}
                        </p>
                      </Row>
                    </div>
                  ))}
                </div>
 
              :
                <div className="align-items-center">   
                  <Loader className="mx-auto" color="#007bff" size="40px" />
                </div>
              } 
            <style jsx>
              {` 
                  .current-price-title{font-size:1.3rem;}
                  .nomintation-modal{font-family: Poppins;}
                  .card-footer-btn{background:#17c671;color:#ffffff;}
                  .btn-lg{border-radius: 0rem;
                    border-top-left-radius: 0rem;
                    border-top-right-radius: 0rem;
                    border-bottom-right-radius: .5rem;
                    border-bottom-left-radius: .5rem;}
                  .card-footer-btn:hover{background:#e6e6e6;color:#000000;}
                  .deposit-text{font-size:.9rem;color:#000;font-weight: 200;text-align:left}
                  .amount-number{font-family:Poppins;color:#007bff;}
              `}                
            </style>
        </>
        );    
      } 

export default ForSaleModal;
