import React from "react";
import { DrizzleContext } from "@drizzle/react-plugin";
import { Drizzle } from "@drizzle/store";
import options from "./drizzleOptions";
import EthAccounts from "./EthAccounts"; 
import UserAccount from "./UserAccount"; 
import { newContextComponents } from "@drizzle/react-components";
const { AccountData, ContractData, ContractForm } = newContextComponents;
const drizzle = new Drizzle(options);

const MyApp = () => {
  return (
    <DrizzleContext.Provider drizzle={drizzle}>
      <DrizzleContext.Consumer>
        {drizzleContext => {
          const {drizzle, drizzleState, initialized} = drizzleContext;

          if(!initialized) {
            return "Loading..."
          }
          const { accounts } = drizzleState;
          return (

            <ContractData
            drizzle={drizzle}
            drizzleState={drizzleState}
            contract="UserStorage"
            method="profiles"
            methodArgs={[accounts[0]]} 
            
            render={displayData => {
              var i = 1;
              const displayObjectProps = [];

              Object.keys(displayData).forEach(key => {
                if (i !== key) {
                  displayObjectProps.push(
                    <li key={i}>
                  
                      value: {`${displayData[key]}`}
                    </li>
                  );
                }

                // i++;
              });
              return <ol>{displayObjectProps}</ol>;
            }}

             //console.log(Object.keys(ObjectTest).map( (key)=> { {key}{ObjectTest[key].name}}));
            //methodArgs={[{ from: accounts[0] }]}
          />
     
          //   <AccountData
          //   drizzle={drizzle}
          //   drizzleState={drizzleState}
          //   accountIndex={0}
          //   units="ether"
          //   precision={3}
          //   render={({ address, balance, units }) => (
          //     <div>
          //       <div>My Address: <span style={{ color: "red" }}>{address}</span></div>
          //       <div>My Ether: <span style={{ color: "red" }}>{balance}</span> {units}</div>
          //     </div>
          //   )}
          // />
            // <EthAccounts drizzle={drizzle} drizzleState={drizzleState} />
            //<UserAccount drizzle={drizzle} drizzleState={drizzleState} />
            )
          }}
      </DrizzleContext.Consumer>
    </DrizzleContext.Provider>
  );
}

export default MyApp;
