const NOM_TX_PROCESSING = 'NOM_TX_PROCESSING'
const TX_PROCESSING_DONE = "TX_PROCESSING_DONE";
const TOKEN_ID_FETCHING = 'TOKEN_ID_FETCHING';
const TX_SUCCESSFUL = 'TX_SUCCESSFUL';
// const CHECK_METAMASK = "CHECK_METAMASK";
// const CHECK_METAMASK_DONE = "CHECK_METAMASK_DONE";
// const TX_ERROR_METAMASK = "TX_ERROR_METAMASK";
// const TX_ERROR_METAMASK_DONE = "TX_ERROR_METAMASK_DONE";
// const TX_SUCCESSFUL_UPDATE_UI = "TX_SUCCESSFUL_UPDATE_UI";
// const CHECKING_TX_UI = "CHECKING_TX_UI";
// const TX_DONE = 'TX_DONE'

const initialState = {
  transactionProcessing:false,
  txSuccessful: false, 
  tokenId: null 
};   
 
function dappReducer(state = initialState, action) {
    switch (action.type) {
      case TX_SUCCESSFUL:
        return { ...state,  txSuccessful: true, transactionProcessing: true };
      case NOM_TX_PROCESSING:
        return { ...state, transactionProcessing: true };
      case TX_PROCESSING_DONE:
        return { ...state, checkMetaMask: false, checkingTx: false,transactionProcessing: false, txSuccessful: true };
      case TOKEN_ID_FETCHING:
          return { ...state, tokenId:action.tokenId};
      default:
        return state;
    }
  }   


export default dappReducer;
