import React from "react";
import { Button } from "shards-react";
import RegModal from '../RegModal.js';
 
 export default class JoinBtnInterestBar extends React.Component {
  constructor(props) {
    super(props);

    this.toggleDropdown = this.toggleDropdown.bind(this);
    this.toggleNavbar = this.toggleNavbar.bind(this);

    this.state = {
      dropdownOpen: false,
      collapseOpen: false,
      showRegisterModal: false,
      ethNetwork: null,
      networkId: null
    };
  }

  
  toggleRegisterModal = async () => {
    const { showRegisterModal } = this.state

    this.setState({
      showRegisterModal: !showRegisterModal,
    })
  }

  toggleDropdown() {
    this.setState({
      ...this.state,
      ...{
        dropdownOpen: !this.state.dropdownOpen
      }
    });
  }

  toggleNavbar() {
    this.setState({
      ...this.state,
      ...{
        collapseOpen: !this.state.collapseOpen
      }
    });
  }

  render() {
    const { showRegisterModal } = this.state;
      return ( 
            <div className="col-sm d-flex justify-content-sm-center"> 
              <Button onClick={this.toggleRegisterModal} onClose={this.toggleRegisterModal} className="join-btn"  outline theme="success" size="md" className="my-2 mr-1">
              Join
              </Button>

              <div>
                {showRegisterModal && (
                  <RegModal onClose={this.toggleRegisterModal} />
                )}
              </div> 
              <style jsx>{` 
                  .card-text {margin-bottom:.1rem;font-family: Poppins;color:black;}
                  .card {width:60%;margin-bottom:0;}
                  .card-body {padding:.5rem;font-size:18px;font-weight:300;} 
                  .arrow {color:#17c671;font-size:19px;font-weight:900;}
                  .join-btn{color:#000;letter-spacing:2px;font-family: Poppins;font-size:14px;line-height: 1;}
                  `}  
        </style>
            </div>  
        )  
    }
}
