import { EventActions, drizzleSagas } from "@drizzle/store";
import { all, fork, takeLatest, put, call, take, race, select } from 'redux-saga/effects';
import { toast } from 'react-toastify';

const TX_DONE = 'TX_DONE'
const TX_PROCESSING = 'TX_PROCESSING'


export const contractEventSaga = store => next => action => {
  if (action.type === EventActions.EVENT_FIRED) {
    const message = action.event.returnValues._message
    const display = `${message}`
 
    toast.success(display, { position: toast.POSITION.BOTTOM_RIGHT })
  }
  return next(action)
}

 
// export const joinTransactionSaga = store => next => action => {
//   if (action.type === 'TX_SUCCESSFUL') {
//     const transactionHash = action.txHash
//     const display = `${transactionHash}`
 
//     toast.success(display, { position: toast.POSITION.BOTTOM_LEFT })
//   }
//   return next(action)
// }


function* showMetaMaskOverlay() {
  yield put({ type: 'CHECK_METAMASK' });
}

function* showTxErrorMsg() {
  yield put({ type: 'TX_ERROR_METAMASK' });
}

function* showCheckingTxMsg() {
  yield put({ type: 'REDIRECT_HOME' });
  yield put({ type: 'CHECKING_TX_UI' });
} 

function* fetchTransaction() {
  yield put({ type: TX_PROCESSING })
 }

 
function* txSuccessful() {
  yield put({ type: 'TX_SUCCESSFUL_UPDATE_UI'});
  while (true) {
    yield put({ type: 'CHECKING_TX_UI_DONE'});  
    const transactionHash = action.txHash
    const display = `${transactionHash}`
    toast.success(display, { position: toast.POSITION.BOTTOM_LEFT })
  }
}

// Combine all your redux concerns

// app root saga
function* appRootSaga(drizzleSagas) {
  drizzleSagas
  yield takeLatest('PUSH_TO_TXSTACK', showMetaMaskOverlay);
  yield takeLatest('TX_ERROR', showTxErrorMsg);
  yield takeLatest('TX_BROADCASTED', fetchTransaction);
  yield takeLatest('TX_SUCCESSFUL', txSuccessful);
  // yield all(
  //  drizzleSagas.map(saga => fork(saga))
  // );
}

export const appSagas = [appRootSaga];
