import React from 'react';
import {Bar} from 'react-chartjs-2';

class PoolInterestBar extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      chartData:props.chartData
    }
  }

  static defaultProps = {
    chartDataBar:{
      labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
      datasets:[
        {
          label:'Pool Depth',
          data:[
            1617200,
            1881121,
            1903932,
            1706677,
            2105099,
            1950333
          ],
          backgroundColor:[
            '#007bff',
            '#007bff',
            '#007bff',
            '#007bff',
            '#007bff',
            '#007bff'
          ],

        }
      ]
    },
    displayTitle:true,
    displayLegend: true,
    legendPosition:'',
    location:''
  }

  render(){
    return (
      <div className="col-sm chart">

        <Bar
            data={this.state.chartData}
            options={{
              legend:{
                display:this.props.displayLegend,
                position:this.props.legendPosition            
              },
              scales: {
                xAxes: [{
                    gridLines: {
                        display:false
                    }
                }],
                yAxes: [{
                    gridLines: {
                        display:true
                    },
                    ticks: {
                      suggestedMin: 100000,
                      suggestedMax: 2200000,
                      stepSize:500000,
                    }

                }],
                scaleLabel:{
                  fontColor:'red'
                }  
            }

            }}
        />
      </div>
    )
  }
}

export default PoolInterestBar;
