import React from 'react';
import {Doughnut} from 'react-chartjs-2';

class AssetTotalDoughNut extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      chartData:props.chartData
    }
  }

  static defaultProps = {
    displayTitle:true,
    displayLegend: true,
    legendPosition:'',
    location:''
  }

  render(){
    return (
      <div className="chart">

        <Doughnut
            data={this.state.chartData}
            options={{
              legend:{
                display:this.props.displayLegend,
                position:this.props.legendPosition            
              },
              scales: {
                xAxes: [{
                    gridLines: {
                        display:false
                    }
                }],
                yAxes: [{
                    gridLines: {
                        display:false
                    },
                    ticks: {
                      suggestedMin: 100000,
                      suggestedMax: 2200000,
                      stepSize:500000,
                    }

                }],
                scaleLabel:{
                  fontColor:'red'
                }  
            }

            }}
        />
      </div>
    )
  }
}

export default AssetTotalDoughNut;
