import React from "react";
import Web3 from "web3";
import { Link, Router } from '../../routes';
import { MetaMaskButton } from 'rimble-ui';
import RegModal from './RegModal.js';
import AccountsContainer from '../EthComponents/AccountsContainer.js';
//import { ethereumButton, showAccount } from '../EthComponents/EnableEthereum.js';
import ConnectionBanner from '@rimble/connection-banner';
import NetworkIndicator from '@rimble/network-indicator';
import {
    Card,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    Button,
    Collapse
  } from "shards-react";

  
var web3 = new Web3(Web3.givenProvider);
var ethNetwork = web3.eth.net.getId();

  export default class NavBar extends React.Component {
    constructor(props) {
      super(props);
  
      this.toggleDropdown = this.toggleDropdown.bind(this);
      this.toggleNavbar = this.toggleNavbar.bind(this);
  
      this.state = {
        dropdownOpen: false,
        collapseOpen: false,
        showRegisterModal: false,
        ethNetwork: null
      };
    }

   
    networkId(){
    var web3 = new Web3(Web3.givenProvider);
    const networkId= web3.eth.net.getId();
    this.setState({ethNetwork: networkId,})
  }
  
    toggleRegisterModal = async () => {
      const { showRegisterModal } = this.state
  
      this.setState({
        showRegisterModal: !showRegisterModal,
      })
    }
  
    toggleDropdown() {
      this.setState({
        ...this.state,
        ...{
          dropdownOpen: !this.state.dropdownOpen
        }
      });
    }
  
    toggleNavbar() {
      this.setState({
        ...this.state,
        ...{
          collapseOpen: !this.state.collapseOpen
        }
      });
    }

   
    render() {
const { showRegisterModal } = this.state;
//       const{ net } = this.state;
// console.log(this.state.ethNetwork); 


console.log({ethNetwork});
      return (   
      <div>
          <Navbar type="dark" theme="primary" expand="md">
            <NavbarBrand href="/">Crypto Asset Consortium</NavbarBrand>
            <NavbarToggler onClick={this.toggleNavbar} />
    
            <Collapse open={this.state.collapseOpen} navbar>
              <Nav navbar>
                <NavItem>
                  <NavLink active href="/assetpool">
                    Interest Asset Pool
                  </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink active href="#">
                    Interest Loan Protocol
                  </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink active href="#">
                    Loan Funding Protocol
                  </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink active href="#">
                    Compound Interest Protocol
                  </NavLink>
                </NavItem>
              </Nav>
              <Nav className="ml-auto">
                <NavItem>
                  
                  {/* <Button outline theme="success" size="md" className="enableEthereumButton my-2 mr-1">
                        Connect Wallet
                  </Button>
                  
                  <Button onClick={this.toggleRegisterModal} outline theme="success" size="md" className="my-2 mr-1">
                      JOIN
                  </Button> */}

                  <NetworkIndicator currentNetwork={ethNetwork} requiredNetwork={5777}>
                    {{
                      onNetworkMessage: "Connected to correct network",
                      noNetworkMessage: "Not connected to anything",
                      onWrongNetworkMessage: "Wrong network"
                    }}
                  </NetworkIndicator>
                  {/* <ConnectionBanner currentNetwork={4} requiredNetwork={1} onWeb3Fallback={true}>
                    {{
                      notWeb3CapableBrowserMessage: (
                        <div>
                          <p>Not a web3 capable browser</p>
                        </div>
                      ),
                      noNetworkAvailableMessage: (
                        <div>
                          <p>No Ethereum network available</p>
                        </div>
                      ),
                      onWrongNetworkMessage: (
                        <div>
                          <p>On wrong Ethereum network</p>
                        </div>
                      )
                    }}
                  </ConnectionBanner> */}
                  <div className="showAccount">
                    {showRegisterModal && (
                      <RegModal onClose={this.toggleRegisterModal} />
                    )}
                  </div> 
      
                </NavItem>
              </Nav>
              <Nav className="ml-auto">
            
                <NavItem>
                  <AccountsContainer />
                </NavItem>
            
              </Nav>
            </Collapse>
          </Navbar>
       
        </div>
      );
    }
  }
