import React, { useState, useEffect } from "react";
import { connect } from 'react-redux';
import { Button } from "shards-react";
import { Loader } from 'rimble-ui';
import { newContextComponents } from "@drizzle/react-components";
const { AccountData, ContractData, ContractForm } = newContextComponents;

const NominationButton = ({ nominateAsset, transactionProcessing, drizzle, drizzleState, tokenId }) => {     

  // let state = drizzle.store.getState();
  // let transactProcessing = state.transactionProcessing;
 
  // useEffect(  () => {

  //   if(transactProcessing){ 
  //     setShow(true);
  //   }
  //   else{
  //   setShow(false);
  //   }
  // }, [transactProcessing]);   
  const getPropAddress = async (tokenId) => {
    const propAddress = await drizzle.contracts.ProposalController.methods.getPropTokenIdMap(tokenId).call();
    return propAddress;
  }
  
  //const member = <ContractData drizzle={drizzle} drizzleState={drizzleState} contract="ProposalController" method="getPropTokenIdMap" methodArgs={[tokenId]} />
  //const propAddress = drizzle.contracts.ProposalController.methods.getPropTokenIdMap(tokenId).call();
  //const Member = <ContractData drizzle={drizzle} drizzleState={drizzleState} contract="ProposalController" method="getProposalIdsFromUser" methodArgs={[1]} />
  if(!transactionProcessing)  {  
    getPropAddress();
  return (
    <div>
    
    <ContractData drizzle={drizzle} drizzleState={drizzleState} contract="ProposalController" method="getPropTokenIdMap" methodArgs={[tokenId]} />
          <Button className="btn btn-block btn-success contribute-form-btn" key="submit" type="button" onClick={nominateAsset}  theme="success" >
            <p className="mb-0">Nominate</p>
          </Button>
          </div>
        )   
  } 
  if(transactionProcessing)  {
    return (   
            <div className="mx-auto">   
              <Loader color="#007bff" size="40px" />
            </div>
        )   
  }

}

const mapStateToProps = (state) => ({
  transactionProcessing: state.appReducers.transactionProcessing,
})

const mapDispatchToProps = (dispatch) => ({
  onSetDrizzleState: (drizzle) => dispatch({ type: "SET_DRIZZLE_STATE", drizzle: drizzle })
});


export default connect(mapStateToProps, mapDispatchToProps)(NominationButton);
