import React from 'react';
import OpenSeaApi from "../api/OpenSeaApi.js"

import {
  Card,
  CardDeck,
  CardHeader,
  CardBody,
  CardFooter,
  Button,
  CardTitle,
  Row,
  Col,
  Table,
  Header,
  HeaderCell,
  Container,
  Progress
} from "shards-react";

export default class ProposalCard extends React.Component {
  constructor(){
    super();
    this.state = {
      error:null,
      isLoaded:false,
      assetData: []
    }
  }


  componentDidMount() {
    fetch("https://api.opensea.io/api/v1/events?only_opensea=true&auction_type=english")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            assets: result.asset_events
          });
        },

        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, assets } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <Progress />
    } else {
      return (
        <CardDeck className="col-sm-8">
          {assets.map(asset=> (
            <Card key={asset.name}>
              <CardBody>
                <h5 className="card-title text-center">{asset.asset_contract.name}</h5>
                  <Row className="asset-image align-items-center">
                    <img className="mx-auto" src= {asset.image_preview_url}/>
                  </Row>
                  <Row className="p-4">
                    <div className="asset-card">{asset.name}</div>
                    <p className="card-text">This card has supporting text below as a natural lead-in to additional content.</p>
                  </Row>
              </CardBody>
              <CardFooter>
                <small className="text-muted">Last updated 3 mins ago</small>
              </CardFooter>
            </Card>          
          ))}

          <style jsx> {` 
            .asset-card{font-weight:400;color:black;font-size:1rem;letter-spacing: .0625rem;font-family: Poppins;}
            `}  
          </style>
        </CardDeck>

    


      );
    }
  }
}
