import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import Web3 from "web3";
import ContributeForm from '../forms/ContributeForm';
import { Loader } from 'rimble-ui';

const ContributeInnnerModal = async ({ props, gotContractBalance, gotPropContract, proposalStorageAbi }) => {
  const { drizzleState, drizzle, propAddress, tokenId, assetPrice } = props;
  var state = drizzle.store.getState()
  let web3 = new Web3(Web3.givenProvider);
  // const account = drizzleState.accounts[0];
  // const manaContract = drizzle.contracts.MANAToken; 
  // const dataKey = manaContract.methods.allowance.cacheCall(account,propAddress);
  // const approveMana = state.contracts.MANAToken.allowance[dataKey].value
  // console.log(approveMana)

  if(tokenId === null){
    return(
      <div className="mx-auto">   
      TokenId{console.log(tokenId)}
        <Loader color="#007bff" size="40px" />
      </div>
    )
  }

  {propAddress && proposalStorageAbi ? 
    useEffect(() => {
      fetchProposalAddress();
    }, [propAddress])

  :
    <div className="mx-auto">   
      {propAddress} ContributeInnerModal 27
      <Loader color="#007bff" size="40px" />
    </div>
  }
const fetchProposalAddress = async () => {
      let proposalAddress = `${propAddress}`;
      const contractConfig = {
        contractName: `${propAddress}`,
        web3Contract: new web3.eth.Contract(proposalStorageAbi, proposalAddress)
      };
      if (!drizzle.contracts[`${propAddress}`]) {
      let events = ['ContributionSuccess','ProposalEvent','LogNewProvableQuery','RandomFailure'] ;  
      drizzle.addContract(contractConfig, events);
      }
      if(drizzle.contracts[`${propAddress}`]){
        const proposalContract = drizzle.contracts[`${propAddress}`];
        const propBalance = await proposalContract.methods.getSummary(`${tokenId}`).call();
        const manaContract = drizzle.contracts.MANAToken; 
        const approvedMana = await manaContract.methods.allowance.cacheCall(account,propAddress);
  
        gotContractBalance(propBalance, approvedMana); 
        gotPropContract(proposalContract);
    }
}

if(propAddress === null || propAddress === 'undefined') {
  return (
    <div className="mx-auto">   
      {propAddress} ContributeInnerModal 52
      <Loader className="mx-auto" color="#007bff" size="40px" />
    </div>
  )
}  
else {
  return ( 
        <>
          <ContributeForm drizzle={drizzle} drizzleState={drizzleState} assetPrice={assetPrice} propAddress={propAddress} tokenId={tokenId} />
        </>
      );    
  } 
}

  const mapStateToProps = (state) => ({
    proposalStorageAbi: state.appReducers.proposalStorageAbi
  })

const mapDispatchToProps = (dispatch) => ({
  gotContractBalance: (propBalance,approvedMana) => dispatch({ type:'PROPOSAL_BALANCE', balance:`${propBalance}`, approvedMana:approvedMana }),
  gotPropContract: (proposalContract) => dispatch({ type:'PROPOSAL_STORAGE_CONTRACT', proposalStorage:proposalContract})
});


export default connect(mapStateToProps, mapDispatchToProps)(ContributeInnnerModal);
