import React, { useState, useEffect } from 'react';
import Modal from 'react-bootstrap/Modal';
import { connect } from 'react-redux';
import Form from 'react-bootstrap/Form';
import {Formik} from 'formik';
import  * as Yup from 'yup';
// import {Col, Button} from 'react-bootstrap'
import { Col } from "shards-react";
import { toast } from 'react-toastify';
import NominationButton from '../buttons/NominateButton';
import ProposalAddress from '../ProposalAddress';

      


const ProposalForm = ({ drizzle, drizzleState, props, propAddress, getCurrentProp, tokenId, assetPrice }) => {
  const [currentProp, setCurrentProp] = useState(null);
  const [tokenIdState, setTokenId] = useState(null);
  // <ProposalAddress drizzle={drizzle} drizzleState={drizzleState} tokenIdState={tokenId} />

const fetchProposalAddress = async () => {
  setTokenId(tokenId);
  if(tokenIdState){
  const proposalAddress = await drizzle.contracts.ProposalController.methods.getPropTokenIdMap(`${tokenIdState}`).call(); 


  
  setCurrentProp(propAddress);
  //return proposalAddress;
  }
  else{
    return 'loading...';
  }
  //setCurrentProp(proposalAddress);
 

  
  };
useEffect(() => {
  fetchProposalAddress();
  getCurrentProp(proposalAddress);
}, [tokenIdState, currentProp]);

console.log('currentProp',currentProp, 'propAddress', propAddress)
  const nominateAsset = async (values) => {
      
    const { text, reSalePrice } = values;
    const listPrice = assetPrice;
    const account = drizzleState.accounts[0];
    const memberBool = await drizzle.contracts.UserStorage.methods.addresses(account).call();
   
    if(memberBool) {
      const user = await drizzle.contracts.UserStorage.methods.profiles(account).call();
      const { userId } = user;
      const contract = await drizzle.contracts.ProposalController;
    try {
      contract.methods["createProposal"].cacheSend(userId, listPrice, reSalePrice, text, tokenId );
 
    } catch (err) {
        return toast.error(`Sorry, we couldn't create asset nomination: ${err}`,{ position: toast.POSITION.TOP_RIGHT })
    }
  }
    else {
      return toast.warning( `Join the Consortium and nominate this asset`, { position: toast.POSITION.TOP_RIGHT });
  }
}


let yup = require('yup');
const schema = yup.object({
  text: yup.string()
  .max(100, 'To many characters')
  .required('Required'),
  reSalePrice: yup.number()
  .typeError("Must be a number")
  .moreThan(2000, 'Must be more than current price.')
  .required('Required'),
  termsOfService: yup.bool().oneOf([true], "You must accept the terms and conditions")
});


if(propAddress === '0x0000000000000000000000000000000000000000' ) {
return (   
    <> 
      <Formik
        initialValues={{
        text: '',
        reSalePrice: '',
        termsOfService:false,
        }}
        validationSchema={schema}
        onSubmit={(values, actions) => {
        nominateAsset(values);
        actions.resetForm();
        addReview(values);
      }}
      >
        {({
          handleSubmit,
          handleChange,
          handleBlur,
          values,
          touched,
          errors,
        }) => (
          <Form noValidate onSubmit={handleSubmit}>
            <Form.Row>
              <Form.Group as={Col} md="6" controlId="validationFormik01">
                <Form.Label>Pitch</Form.Label>
                <Form.Control
                  type="text"
                  as="textarea" 
                  rows={3}
                  name="text"
                  placeholder='100 Character Max'
                  value={values.text}
                  onChange={handleChange}
                  onBlur={handleBlur}
                  isValid={values.text}
                  isInvalid={touched.text && !!errors.text}
                  />
                <Form.Control.Feedback type="invalid">
                  {errors.text}
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group as={Col} md="4" controlId="validationFormik02">
                <Form.Label>Resale Price</Form.Label>
                <Form.Control
                  type="text" 
                  name="reSalePrice"
                  placeholder="Resale Price"
                  value={values.reSalePrice}
                  onChange={handleChange}
                  onBlur={handleBlur}
                  isInvalid={touched.reSalePrice && !!errors.reSalePrice}
                  isValid={values.reSalePrice}
                />
                <Form.Control.Feedback type="invalid">  
                {errors.reSalePrice}
                </Form.Control.Feedback>
              </Form.Group>
            </Form.Row>
            <Form.Group>
              <Form.Check
                required
                name="termsOfService"
                label="Agree to terms and conditions"
                onChange={handleChange}
                isInvalid={!!errors.termsOfService}
                feedback={errors.termsOfService}
                id="validationFormik0"
              />
            </Form.Group>
            <Modal.Footer>
              <NominationButton props={props} tokenId={tokenId} />
            </Modal.Footer>
          </Form>
        )}
      </Formik>
    </>
  );   
        }
        
        else{
          return (
          <div>
         {propAddress}
          </div>
          )
        }
} 

  const mapStateToProps = (state) => ({
    propAddress: state.appReducers.propAddress,
  })
  
  const mapDispatchToProps = (dispatch) => ({
    getCurrentProp: (proposalAddress) =>  dispatch({ type: "GOT_PROP_ADDRESS", proposalAddress:proposalAddress })
  });
        
        
export default connect(mapStateToProps, mapDispatchToProps)(ProposalForm);

    //export default ProposalForm;
